import type React from "react"
import type { Metadata } from "next"
import { GeistSans } from "geist/font/sans"
import { GeistMono } from "geist/font/mono"
import { Analytics } from "@vercel/analytics/next"
import "./globals.css"
import { ThemeProvider } from "@/components/theme-provider"
import { Suspense } from "react"

export const metadata: Metadata = {
  title: "HZ Shop Management",
  description: "Professional retail management solution with offline capabilities",
  generator: "v0.app",
  manifest: "/manifest.json",
  themeColor: "#0891b2",
  viewport: "width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no",
  appleWebApp: {
    capable: true,
    statusBarStyle: "default",
    title: "HZ Shop",
  },
  icons: {
    icon: "/icon-192.png",
    apple: "/icon-192.png",
  },
}

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode
}>) {
  return (
    <html lang="en">
      <head>
        <link rel="manifest" href="/manifest.json" />
        <meta name="theme-color" content="#0891b2" />
        <meta name="apple-mobile-web-app-capable" content="yes" />
        <meta name="apple-mobile-web-app-status-bar-style" content="default" />
        <meta name="apple-mobile-web-app-title" content="HZ Shop" />
        <link rel="apple-touch-icon" href="/icon-192.png" />
      </head>
      <body className={`font-sans ${GeistSans.variable} ${GeistMono.variable}`}>
        <Suspense fallback={null}>
          <ThemeProvider attribute="class" defaultTheme="light" enableSystem disableTransitionOnChange>
            {children}
          </ThemeProvider>
        </Suspense>
        <Analytics />
        <script
          dangerouslySetInnerHTML={{
            __html: `
              if ('serviceWorker' in navigator) {
                window.addEventListener('load', function() {
                  navigator.serviceWorker.register('/sw.js')
                    .then(function(registration) {
                      console.log('[v0] SW registered: ', registration);
                    })
                    .catch(function(registrationError) {
                      console.log('[v0] SW registration failed: ', registrationError);
                    });
                });
              }
            `,
          }}
        />
      </body>
    </html>
  )
}

app/page.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Header } from "@/components/layout/header"
import { Sidebar } from "@/components/layout/sidebar"
import { BottomNav } from "@/components/layout/bottom-nav"
import { FloatingActions } from "@/components/layout/floating-actions"
import { Dashboard } from "@/components/pages/dashboard"
import { Products } from "@/components/pages/products"
import { Categories } from "@/components/pages/categories"
import { Receipts } from "@/components/pages/receipts"
import { Returns } from "@/components/pages/returns"
import { Shopkeepers } from "@/components/pages/shopkeepers"
import { Reports } from "@/components/pages/reports"
import { Settings } from "@/components/pages/settings"
import { PublicCatalog } from "@/components/pages/public-catalog"
import { PWAInstallPrompt } from "@/components/pwa-install-prompt"
import { ConnectionStatus } from "@/components/connection-status"
import { syncManager } from "@/lib/sync-manager"
import { offlineStorage } from "@/lib/offline-storage"
import { Toaster } from "@/components/ui/toaster"
import { cn } from "@/lib/utils"

export default function ShopManagement() {
  const [currentPage, setCurrentPage] = useState("dashboard")
  const [sidebarCollapsed, setSidebarCollapsed] = useState(false)
  const [offlineMode, setOfflineMode] = useState(false)
  const [selectedCategory, setSelectedCategory] = useState<string | null>(null)
  const [isInitialized, setIsInitialized] = useState(false)

  useEffect(() => {
    const initializeApp = async () => {
      try {
        await offlineStorage.init()
        console.log("[v0] Offline storage initialized")

        // Try to sync from server if online
        if (navigator.onLine) {
          await syncManager.syncFromServer()
          console.log("[v0] Initial sync from server completed")
        }

        setIsInitialized(true)
      } catch (error) {
        console.error("[v0] Failed to initialize app:", error)
        setIsInitialized(true) // Still allow app to work
      }
    }

    initializeApp()
  }, [])

  const handleNavigateToProducts = (category?: string) => {
    setSelectedCategory(category || null)
    setCurrentPage("products")
  }

  const renderPage = () => {
    if (!isInitialized) {
      return (
        <div className="flex items-center justify-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">Initializing HZ Shop...</p>
          </div>
        </div>
      )
    }

    switch (currentPage) {
      case "dashboard":
        return <Dashboard />
      case "products":
        return <Products selectedCategory={selectedCategory} onClearCategory={() => setSelectedCategory(null)} />
      case "categories":
        return <Categories onNavigateToProducts={handleNavigateToProducts} />
      case "receipts":
        return <Receipts />
      case "returns":
        return <Returns />
      case "shopkeepers":
        return <Shopkeepers />
      case "reports":
        return <Reports />
      case "settings":
        return <Settings />
      case "catalog":
        return <PublicCatalog />
      default:
        return <Dashboard />
    }
  }

  const getBreadcrumbs = () => {
    const breadcrumbMap: Record<string, string[]> = {
      dashboard: ["Dashboard"],
      products: ["Dashboard", "Products"],
      categories: ["Dashboard", "Categories"],
      receipts: ["Dashboard", "Receipts"],
      returns: ["Dashboard", "Returns"],
      shopkeepers: ["Dashboard", "Shopkeepers"],
      reports: ["Dashboard", "Reports"],
      settings: ["Dashboard", "Settings"],
      catalog: ["Public Catalog"],
    }
    return breadcrumbMap[currentPage] || ["Dashboard"]
  }

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <Header
        offlineMode={offlineMode}
        onToggleOffline={() => setOfflineMode(!offlineMode)}
        sidebarCollapsed={sidebarCollapsed}
        onToggleSidebar={() => setSidebarCollapsed(!sidebarCollapsed)}
      />

      <div className="flex">
        {/* Sidebar */}
        <Sidebar currentPage={currentPage} onPageChange={setCurrentPage} collapsed={sidebarCollapsed} />

        {/* Main Content */}
        <main
          className={cn(
            "flex-1 transition-all duration-300",
            sidebarCollapsed ? "ml-16" : "ml-64",
            "pt-16 pb-20 md:pb-4",
          )}
        >
          {/* Breadcrumbs */}
          <div className="px-6 py-4 border-b border-border flex items-center justify-between">
            <nav className="flex items-center space-x-2 text-sm text-muted-foreground">
              {getBreadcrumbs().map((crumb, index) => (
                <div key={crumb} className="flex items-center">
                  {index > 0 && <span className="mx-2">/</span>}
                  <span className={index === getBreadcrumbs().length - 1 ? "text-foreground font-medium" : ""}>
                    {crumb}
                  </span>
                </div>
              ))}
            </nav>
            <ConnectionStatus />
          </div>

          {/* Page Content */}
          <div className="p-6">{renderPage()}</div>
        </main>
      </div>

      {/* Bottom Navigation (Mobile) */}
      <BottomNav currentPage={currentPage} onPageChange={setCurrentPage} />

      {/* Floating Action Buttons */}
      <FloatingActions />

      <PWAInstallPrompt />

      {/* Toast Notifications */}
      <Toaster />
    </div>
  )
}

components/connection-status.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Wifi, WifiOff, RefreshCw, Loader2 } from "lucide-react"
import { Badge } from "@/components/ui/badge"
import { Button } from "@/components/ui/button"
import { syncManager } from "@/lib/sync-manager"
import { useToast } from "@/hooks/use-toast"

export function ConnectionStatus() {
  const [isOnline, setIsOnline] = useState(true)
  const [isSyncing, setIsSyncing] = useState(false)
  const { toast } = useToast()

  useEffect(() => {
    const updateOnlineStatus = () => {
      const online = navigator.onLine
      setIsOnline(online)

      if (online) {
        toast({
          title: "Back Online",
          description: "Connection restored. Data will sync automatically.",
        })
      } else {
        toast({
          title: "Gone Offline",
          description: "Working in offline mode. Changes will sync when reconnected.",
          variant: "destructive",
        })
      }
    }

    // Set initial status
    setIsOnline(navigator.onLine)

    window.addEventListener("online", updateOnlineStatus)
    window.addEventListener("offline", updateOnlineStatus)

    return () => {
      window.removeEventListener("online", updateOnlineStatus)
      window.removeEventListener("offline", updateOnlineStatus)
    }
  }, [toast])

  const handleSync = async () => {
    if (!isOnline) {
      toast({
        title: "Cannot Sync",
        description: "No internet connection. Please check your network.",
        variant: "destructive",
      })
      return
    }

    setIsSyncing(true)
    try {
      await syncManager.manualSync()
      toast({
        title: "Sync Complete",
        description: "All data synchronized successfully.",
      })
    } catch (error) {
      console.error("[v0] Manual sync failed:", error)
      toast({
        title: "Sync Failed",
        description: "Failed to synchronize data. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsSyncing(false)
    }
  }

  return (
    <div className="flex items-center gap-2">
      <Badge variant={isOnline ? "default" : "destructive"} className="flex items-center gap-1">
        {isOnline ? <Wifi className="h-3 w-3" /> : <WifiOff className="h-3 w-3" />}
        {isOnline ? "Online" : "Offline"}
      </Badge>

      <Button
        variant="ghost"
        size="sm"
        onClick={handleSync}
        disabled={!isOnline || isSyncing}
        className="h-6 px-2"
        title={isOnline ? "Sync data with server" : "Cannot sync while offline"}
      >
        {isSyncing ? <Loader2 className="h-3 w-3 animate-spin" /> : <RefreshCw className="h-3 w-3" />}
      </Button>
    </div>
  )
}

components/pwa-install-prompt.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Download, X } from "lucide-react"
import { Button } from "@/components/ui/button"
import { Card, CardContent } from "@/components/ui/card"

interface BeforeInstallPromptEvent extends Event {
  prompt(): Promise<void>
  userChoice: Promise<{ outcome: "accepted" | "dismissed" }>
}

export function PWAInstallPrompt() {
  const [deferredPrompt, setDeferredPrompt] = useState<BeforeInstallPromptEvent | null>(null)
  const [showPrompt, setShowPrompt] = useState(false)

  useEffect(() => {
    const handler = (e: Event) => {
      e.preventDefault()
      setDeferredPrompt(e as BeforeInstallPromptEvent)
      setShowPrompt(true)
    }

    window.addEventListener("beforeinstallprompt", handler)

    return () => {
      window.removeEventListener("beforeinstallprompt", handler)
    }
  }, [])

  const handleInstall = async () => {
    if (!deferredPrompt) return

    deferredPrompt.prompt()
    const { outcome } = await deferredPrompt.userChoice

    console.log("[v0] PWA install outcome:", outcome)
    setDeferredPrompt(null)
    setShowPrompt(false)
  }

  const handleDismiss = () => {
    setShowPrompt(false)
    setDeferredPrompt(null)
  }

  if (!showPrompt || !deferredPrompt) return null

  return (
    <Card className="fixed bottom-4 left-4 right-4 z-50 mx-auto max-w-sm border-primary/20 bg-background/95 backdrop-blur supports-[backdrop-filter]:bg-background/60">
      <CardContent className="flex items-center gap-3 p-4">
        <div className="flex h-10 w-10 items-center justify-center rounded-lg bg-primary/10">
          <Download className="h-5 w-5 text-primary" />
        </div>
        <div className="flex-1">
          <h3 className="font-semibold text-sm">Install HZ Shop</h3>
          <p className="text-xs text-muted-foreground">Add to home screen for quick access</p>
        </div>
        <div className="flex gap-2">
          <Button size="sm" onClick={handleInstall}>
            Install
          </Button>
          <Button size="sm" variant="ghost" onClick={handleDismiss}>
            <X className="h-4 w-4" />
          </Button>
        </div>
      </CardContent>
    </Card>
  )
}

components/sync-button.tsx.content:
"use client"

import { useState } from "react"
import { Button } from "@/components/ui/button"
import { Loader2, RefreshCw, Wifi, WifiOff } from "lucide-react"
import { syncManager } from "@/lib/sync-manager"
import { useToast } from "@/hooks/use-toast"

interface SyncButtonProps {
  isOnline: boolean
}

export function SyncButton({ isOnline }: SyncButtonProps) {
  const [isSyncing, setIsSyncing] = useState(false)
  const { toast } = useToast()

  const handleSync = async () => {
    if (!isOnline) {
      toast({
        title: "Offline",
        description: "Cannot sync while offline. Please check your internet connection.",
        variant: "destructive",
      })
      return
    }

    setIsSyncing(true)
    try {
      await syncManager.manualSync()
      toast({
        title: "Sync Complete",
        description: "All data has been synchronized with the server.",
      })
    } catch (error) {
      console.error("[v0] Manual sync failed:", error)
      toast({
        title: "Sync Failed",
        description: "Failed to synchronize data. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsSyncing(false)
    }
  }

  return (
    <Button
      onClick={handleSync}
      disabled={!isOnline || isSyncing}
      variant={isOnline ? "default" : "secondary"}
      size="sm"
      className="gap-2"
    >
      {isSyncing ? (
        <Loader2 className="h-4 w-4 animate-spin" />
      ) : (
        <>
          {isOnline ? <Wifi className="h-4 w-4" /> : <WifiOff className="h-4 w-4" />}
          <RefreshCw className="h-4 w-4" />
        </>
      )}
      {isSyncing ? "Syncing..." : "Sync"}
    </Button>
  )
}

