}

components/modals/add-category-modal.tsx.content:
"use client"

import type React from "react"

import { useState } from "react"
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { useToast } from "@/hooks/use-toast"
import { addCategory } from "@/lib/database"

interface AddCategoryModalProps {
  onClose: () => void
  onCategoryAdded?: () => void
}

export function AddCategoryModal({ onClose, onCategoryAdded }: AddCategoryModalProps) {
  const [formData, setFormData] = useState({
    name: "",
    description: "",
    image_url: "",
  })
  const [isLoading, setIsLoading] = useState(false)
  const { toast } = useToast()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsLoading(true)

    try {
      await addCategory({
        name: formData.name.trim(),
        description: formData.description.trim() || undefined,
        image_url: formData.image_url.trim() || undefined,
      })

      toast({
        title: "Category Added",
        description: `${formData.name} has been added successfully.`,
      })

      if (onCategoryAdded) {
        onCategoryAdded()
      } else {
        onClose()
      }
    } catch (error) {
      console.error("[v0] Failed to add category:", error)
      toast({
        title: "Error",
        description: "Failed to add category. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Add New Category</DialogTitle>
          <DialogDescription>Create a new product category to organize your inventory.</DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div className="space-y-2">
            <Label htmlFor="name">Category Name *</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData((prev) => ({ ...prev, name: e.target.value }))}
              placeholder="e.g., Smartphones, Laptops, Accessories"
              required
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="description">Description</Label>
            <Textarea
              id="description"
              value={formData.description}
              onChange={(e) => setFormData((prev) => ({ ...prev, description: e.target.value }))}
              placeholder="Brief description of this category"
              rows={3}
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="image_url">Image URL</Label>
            <Input
              id="image_url"
              type="url"
              value={formData.image_url}
              onChange={(e) => setFormData((prev) => ({ ...prev, image_url: e.target.value }))}
              placeholder="https://example.com/image.jpg"
            />
            {formData.image_url && (
              <div className="mt-2">
                <img
                  src={formData.image_url || "/placeholder.svg?height=200&width=300&query=category"}
                  alt="Category preview"
                  className="w-full h-32 object-cover rounded-lg border"
                  onError={(e) => {
                    e.currentTarget.src = "/abstract-categories.png"
                  }}
                />
              </div>
            )}
          </div>

          <div className="flex justify-end gap-3">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading || !formData.name.trim()}>
              {isLoading ? "Adding..." : "Add Category"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  )
}

components/modals/create-receipt-modal.t.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { Badge } from "@/components/ui/badge"
import { X, Trash2, Search, RotateCcw, Edit2 } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { syncManager } from "@/lib/sync-manager"
import { getProducts } from "@/lib/database"

interface CreateReceiptModalProps {
  onClose: () => void
  onCreateReceipt: (receipt: any) => void
  onCreateReturn?: (returnData: any) => void
  cartItems?: any[]
}

interface ReceiptItem {
  productId: number
  productName: string
  price: number
  quantity: number
  total: number
}

interface ReturnItem {
  productId: number
  productName: string
  quantity: number
  amount: number
  originalPrice: number
}

export function CreateReceiptModal({ onClose, onCreateReceipt, onCreateReturn, cartItems }: CreateReceiptModalProps) {
  const [products, setProducts] = useState<any[]>([])
  const [loadingProducts, setLoadingProducts] = useState(true)
  const [shopkeepers, setShopkeepers] = useState<any[]>([])
  const [customerSuggestions, setCustomerSuggestions] = useState<any[]>([])
  const [showCustomerSuggestions, setShowCustomerSuggestions] = useState(false)

  const [customerName, setCustomerName] = useState("")
  const [customerPhone, setCustomerPhone] = useState("")
  const [items, setItems] = useState<ReceiptItem[]>([])
  const [discount, setDiscount] = useState(0)
  const [amountReceived, setAmountReceived] = useState(0)
  const [notes, setNotes] = useState("")
  const [productSearch, setProductSearch] = useState("")
  const [returnItems, setReturnItems] = useState<ReturnItem[]>([])
  const [showReturnForm, setShowReturnForm] = useState(false)
  const [selectedReturnProduct, setSelectedReturnProduct] = useState<number | null>(null)
  const [returnQuantity, setReturnQuantity] = useState(1)
  const [returnProductPrice, setReturnProductPrice] = useState(0)
  const [returnProductSearch, setReturnProductSearch] = useState("")
  const [editingPrice, setEditingPrice] = useState<number | null>(null)
  const [tempPrice, setTempPrice] = useState("")
  const [editingReturnPrice, setEditingReturnPrice] = useState<number | null>(null)
  const [tempReturnPrice, setTempReturnPrice] = useState("")
  const [previousReceipts, setPreviousReceipts] = useState<any[]>([])
  const { toast } = useToast()

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoadingProducts(true)
        console.log("[v0] Loading receipt modal data...")

        const [syncProducts, dbProducts, shopkeepersData] = await Promise.all([
          syncManager.getProducts(),
          getProducts(),
          syncManager.getShopkeepers(),
        ])

        // Combine products from both sources, preferring sync manager data
        const allProducts = [...(syncProducts || []), ...(dbProducts || [])]
        const uniqueProducts = allProducts.filter(
          (product, index, self) => index === self.findIndex((p) => p.id === product.id),
        )

        console.log("[v0] Loaded products for receipt:", uniqueProducts.length)
        setProducts(uniqueProducts)
        setShopkeepers(shopkeepersData || [])
      } catch (error) {
        console.error("[v0] Failed to load data for receipt:", error)
        toast({
          title: "Error",
          description: "Failed to load data. Please try again.",
          variant: "destructive",
        })
      } finally {
        setLoadingProducts(false)
      }
    }

    loadData()
  }, [toast])

  useEffect(() => {
    const loadReceipts = async () => {
      try {
        const receiptsData = await syncManager.getReceipts()
        setPreviousReceipts(receiptsData || [])
      } catch (error) {
        console.error("[v0] Failed to load receipts for returns:", error)
      }
    }

    loadReceipts()
  }, [])

  useEffect(() => {
    if (cartItems && cartItems.length > 0) {
      const cartReceiptItems = cartItems.map((item) => ({
        productId: item.product_id,
        productName: item.product?.name || "Unknown Product",
        price: item.product?.selling_price || 0,
        quantity: item.quantity,
        total: (item.product?.selling_price || 0) * item.quantity,
      }))
      setItems(cartReceiptItems)
      console.log("[v0] Auto-populated receipt with cart items:", cartReceiptItems.length)
    }
  }, [cartItems])

  const handleCustomerNameChange = (value: string) => {
    setCustomerName(value)

    if (value.length > 0) {
      const suggestions = shopkeepers
        .filter((shopkeeper) => shopkeeper.name.toLowerCase().includes(value.toLowerCase()))
        .slice(0, 5)
      setCustomerSuggestions(suggestions)
      setShowCustomerSuggestions(suggestions.length > 0)
    } else {
      setShowCustomerSuggestions(false)
    }
  }

  const selectCustomer = (shopkeeper: any) => {
    setCustomerName(shopkeeper.name)
    setCustomerPhone(shopkeeper.phone)
    setShowCustomerSuggestions(false)
  }

  const addItem = async (product: any) => {
    const requestedQuantity = 1
    const existingItem = items.find((item) => item.productId === product.id)
    const totalQuantity = existingItem ? existingItem.quantity + requestedQuantity : requestedQuantity

    if (product.current_stock < totalQuantity) {
      toast({
        title: "Insufficient Stock",
        description: `Only ${product.current_stock} units available for ${product.name}`,
        variant: "destructive",
      })
      return
    }

    if (existingItem) {
      setItems(
        items.map((item) =>
          item.productId === product.id
            ? { ...item, quantity: item.quantity + 1, total: (item.quantity + 1) * item.price }
            : item,
        ),
      )
    } else {
      setItems([
        ...items,
        {
          productId: product.id,
          productName: product.name,
          price: product.selling_price || 0,
          quantity: 1,
          total: product.selling_price || 0,
        },
      ])
    }
    setProductSearch("")
  }

  const updateItemQuantity = (productId: number, quantity: number) => {
    if (quantity <= 0) {
      removeItem(productId)
      return
    }

    const product = products.find((p) => p.id === productId)
    if (product && product.current_stock < quantity) {
      toast({
        title: "Insufficient Stock",
        description: `Only ${product.current_stock} units available for ${product.name}`,
        variant: "destructive",
      })
      return
    }

    setItems(
      items.map((item) => (item.productId === productId ? { ...item, quantity, total: quantity * item.price } : item)),
    )
  }

  const updateItemPrice = (productId: number, newPrice: number) => {
    setItems(
      items.map((item) =>
        item.productId === productId ? { ...item, price: newPrice, total: item.quantity * newPrice } : item,
      ),
    )
  }

  const startEditingPrice = (productId: number, currentPrice: number) => {
    setEditingPrice(productId)
    setTempPrice(currentPrice.toString())
  }

  const savePrice = (productId: number) => {
    const newPrice = Number.parseFloat(tempPrice) || 0
    if (newPrice > 0) {
      updateItemPrice(productId, newPrice)
    }
    setEditingPrice(null)
    setTempPrice("")
  }

  const cancelEditingPrice = () => {
    setEditingPrice(null)
    setTempPrice("")
  }

  const removeItem = (productId: number) => {
    setItems(items.filter((item) => item.productId !== productId))
  }

  const updateReturnItemPrice = (productId: number, newPrice: number) => {
    setReturnItems(
      returnItems.map((item) => (item.productId === productId ? { ...item, amount: item.quantity * newPrice } : item)),
    )
  }

  const startEditingReturnPrice = (productId: number, currentAmount: number, quantity: number) => {
    setEditingReturnPrice(productId)
    setTempReturnPrice((currentAmount / quantity).toString())
  }

  const saveReturnPrice = (productId: number, quantity: number) => {
    const newPrice = Number.parseFloat(tempReturnPrice) || 0
    if (newPrice > 0) {
      updateReturnItemPrice(productId, newPrice)
    }
    setEditingReturnPrice(null)
    setTempReturnPrice("")
  }

  const cancelEditingReturnPrice = () => {
    setEditingReturnPrice(null)
    setTempReturnPrice("")
  }

  const removeReturnItem = (productId: number) => {
    setReturnItems(returnItems.filter((item) => item.productId !== productId))
  }

  const subtotal = items.reduce((sum, item) => sum + item.total, 0)
  const returnTotal = returnItems.reduce((sum, item) => sum + item.amount, 0)
  const total = subtotal - discount - returnTotal
  const pendingAmount = Math.max(0, total - amountReceived)

  const handleSubmit = async () => {
    console.log("[v0] Starting receipt creation...")
    console.log("[v0] Customer:", customerName, customerPhone)
    console.log("[v0] Items:", items.length)

    if (!customerName || !customerPhone || items.length === 0) {
      console.log("[v0] Validation failed - missing required fields")
      toast({
        title: "Missing Information",
        description: "Please fill in all required fields and add at least one item.",
        variant: "destructive",
      })
      return
    }

    try {
      console.log("[v0] Validating stock...")
      const stockValidation = await validateStockForReceipt(
        items.map((item) => ({ productId: item.productId.toString(), quantity: item.quantity })),
      )

      if (!stockValidation.isValid) {
        console.log("[v0] Stock validation failed:", stockValidation.invalidItems)
        const errorMessage = stockValidation.invalidItems
          .map((item) => `${item.productName}: Need ${item.requestedQuantity}, Available ${item.availableStock}`)
          .join("\n")

        toast({
          title: "Insufficient Stock",
          description: errorMessage,
          variant: "destructive",
        })
        return
      }

      console.log("[v0] Creating receipt...")
      const newReceipt = {
        id: `RCP-${Date.now()}`,
        receiptNumber: `RCP-${Date.now()}`,
        shopName: "HZ Shop",
        customerName,
        customerPhone,
        items,
        returnItems,
        subtotal,
        discount,
        returnTotal,
        total,
        receivedAmount: amountReceived,
        changeAmount: Math.max(0, amountReceived - total),
        notes,
        date: new Date().toISOString(),
        status: "Completed",
        createdBy: "Admin",
      }

      console.log("[v0] Saving receipt to sync manager...")
      await syncManager.saveReceipt(newReceipt)

      console.log("[v0] Updating product stock...")
      for (const item of items) {
        await updateProductStock(item.productId.toString(), item.quantity)
      }

      onCreateReceipt(newReceipt)

      console.log("[v0] Creating/updating shopkeeper...")
      await syncManager.createOrUpdateShopkeeper({
        name: customerName,
        phone: customerPhone,
        receiptNumber: newReceipt.receiptNumber,
        receiptDate: newReceipt.date,
        totalAmount: total,
        amountReceived,
        pendingAmount: Math.max(0, total - amountReceived),
      })

      window.dispatchEvent(
        new CustomEvent("update-shopkeeper-balance", {
          detail: {
            shopkeeperName: customerName,
            shopkeeperPhone: customerPhone,
            receiptNumber: newReceipt.receiptNumber,
            receiptDate: newReceipt.date,
            totalAmount: total,
            amountReceived,
            pendingAmount: Math.max(0, total - amountReceived),
          },
        }),
      )

      if (returnItems.length > 0) {
        for (const returnItem of returnItems) {
          const returnData = {
            id: `RT-${Date.now()}-${returnItem.productId}`,
            receiptId: newReceipt.receiptNumber,
            customerName,
            customerPhone,
            productName: returnItem.productName,
            productId: returnItem.productId,
            quantity: returnItem.quantity,
            amount: returnItem.amount,
            reason: "Return processed during receipt creation",
            description: "Return item processed automatically",
            date: new Date().toISOString(),
            status: "approved",
            images: [],
            approvedBy: "System",
            processedDate: new Date().toISOString(),
            refundMethod: "cash",
            notes: `Return created during receipt generation for ${newReceipt.receiptNumber}`,
          }

          await syncManager.saveReturn(returnData)
          if (onCreateReturn) {
            onCreateReturn(returnData)
          }
        }
      }

      console.log("[v0] Receipt created successfully!")
      toast({
        title: "Receipt Created",
        description: `Receipt ${newReceipt.receiptNumber} for ${customerName} has been created successfully.${
          returnItems.length > 0 ? ` ${returnItems.length} return(s) also recorded.` : ""
        }`,
      })
      onClose()
    } catch (error) {
      console.error("[v0] Failed to create receipt:", error)
      toast({
        title: "Error",
        description: `Failed to create receipt: ${error.message || "Unknown error"}`,
        variant: "destructive",
      })
    }
  }

  const handleAddReturn = () => {
    if (!selectedReturnProduct || returnProductPrice <= 0) {
      toast({
        title: "Missing Return Information",
        description: "Please select a product and enter a valid return price.",
        variant: "destructive",
      })
      return
    }

    const allReceiptItems = previousReceipts.flatMap(
      (receipt) =>
        receipt.items?.map((item: any) => ({
          ...item,
          receiptId: receipt.receiptNumber,
          receiptDate: receipt.date,
          customerName: receipt.customerName,
        })) || [],
    )

    const selectedProduct = allReceiptItems.find((item) => item.productId === selectedReturnProduct)
    if (!selectedProduct) return

    const newReturn: ReturnItem = {
      productId: selectedReturnProduct,
      productName: selectedProduct.productName,
      quantity: returnQuantity,
      amount: returnProductPrice * returnQuantity,
      originalPrice: selectedProduct.price,
    }

    setReturnItems([...returnItems, newReturn])
    setSelectedReturnProduct(null)
    setReturnQuantity(1)
    setReturnProductPrice(0)
    setReturnProductSearch("")
    setShowReturnForm(false)

    toast({
      title: "Return Added",
      description: `Return for ${selectedProduct.productName} has been added to this receipt.`,
    })
  }

  const filteredProducts = products.filter((product) =>
    product.name?.toLowerCase().includes(productSearch.toLowerCase()),
  )

  const allReceiptItems = previousReceipts.flatMap(
    (receipt) =>
      receipt.items?.map((item: any) => ({
        ...item,
        receiptId: receipt.receiptNumber,
        receiptDate: receipt.date,
        customerName: receipt.customerName,
      })) || [],
  )

  const filteredPreviousItems = allReceiptItems.filter((item) =>
    item.productName?.toLowerCase().includes(returnProductSearch.toLowerCase()),
  )

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
      <Card className="w-full max-w-4xl max-h-[90vh] overflow-hidden">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <div>
            <CardTitle>Create New Receipt</CardTitle>
            <CardDescription>Generate a new sales receipt</CardDescription>
          </div>
          <Button variant="ghost" size="sm" onClick={onClose}>
            <X className="h-4 w-4" />
          </Button>
        </CardHeader>
        <CardContent className="overflow-y-auto max-h-[calc(90vh-120px)] space-y-6">
          {/* Customer Information */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2 relative">
              <Label htmlFor="customerName">Customer Name *</Label>
              <Input
                id="customerName"
                value={customerName}
                onChange={(e) => handleCustomerNameChange(e.target.value)}
                onBlur={() => setTimeout(() => setShowCustomerSuggestions(false), 200)}
                onFocus={() => customerName.length > 0 && setShowCustomerSuggestions(customerSuggestions.length > 0)}
                placeholder="Enter customer name"
              />
              {showCustomerSuggestions && (
                <div className="absolute top-full left-0 right-0 z-10 mt-1 border rounded-lg bg-white shadow-lg max-h-40 overflow-y-auto">
                  {customerSuggestions.map((shopkeeper) => (
                    <div
                      key={shopkeeper.id}
                      className="p-3 hover:bg-muted cursor-pointer border-b last:border-b-0"
                      onClick={() => selectCustomer(shopkeeper)}
                    >
                      <div className="flex justify-between items-center">
                        <div>
                          <p className="font-medium">{shopkeeper.name}</p>
                          <p className="text-sm text-muted-foreground">{shopkeeper.phone}</p>
                        </div>
                        <Badge variant="outline">{shopkeeper.totalOrders || 0} orders</Badge>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
            <div className="space-y-2">
              <Label htmlFor="customerPhone">Customer Phone *</Label>
              <Input
                id="customerPhone"
                value={customerPhone}
                onChange={(e) => setCustomerPhone(e.target.value)}
                placeholder="Enter phone number"
              />
            </div>
          </div>

          {/* Product Selection */}
          <div className="space-y-4">
            <div>
              <Label htmlFor="productSearch">Add Products</Label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  id="productSearch"
                  value={productSearch}
                  onChange={(e) => setProductSearch(e.target.value)}
                  placeholder={loadingProducts ? "Loading products..." : "Search products to add..."}
                  className="pl-10"
                  disabled={loadingProducts}
                />
              </div>
              {productSearch && !loadingProducts && (
                <div className="mt-2 border rounded-lg max-h-40 overflow-y-auto">
                  {filteredProducts.length > 0 ? (
                    filteredProducts.map((product) => (
                      <div
                        key={product.id}
                        className="p-3 hover:bg-muted cursor-pointer border-b last:border-b-0"
                        onClick={() => addItem(product)}
                      >
                        <div className="flex justify-between items-center">
                          <div>
                            <p className="font-medium">{product.name}</p>
                            <p className="text-sm text-muted-foreground">
                              PKR {(product.selling_price || 0).toLocaleString()}
                            </p>
                          </div>
                          <Badge variant="outline">{product.current_stock || 0} in stock</Badge>
                        </div>
                      </div>
                    ))
                  ) : (
                    <div className="p-3 text-center text-muted-foreground">
                      No products found matching "{productSearch}"
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Selected Items */}
            {items.length > 0 && (
              <div className="space-y-2">
                <div className="flex items-center justify-between">
                  <Label>Selected Items</Label>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setShowReturnForm(!showReturnForm)}
                    className="text-orange-600 border-orange-600 hover:bg-orange-50"
                  >
                    <RotateCcw className="h-4 w-4 mr-2" />
                    Return Product
                  </Button>
                </div>
                <div className="border rounded-lg">
                  {items.map((item) => (
                    <div key={item.productId} className="p-3 border-b last:border-b-0">
                      <div className="flex items-center justify-between">
                        <div className="flex-1">
                          <p className="font-medium">{item.productName}</p>
                          <div className="flex items-center gap-2">
                            {editingPrice === item.productId ? (
                              <div className="flex items-center gap-2">
                                <Input
                                  type="number"
                                  value={tempPrice}
                                  onChange={(e) => setTempPrice(e.target.value)}
                                  className="w-24 h-6 text-xs"
                                  placeholder="Price"
                                />
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() => savePrice(item.productId)}
                                  className="h-6 px-2"
                                >
                                  ✓
                                </Button>
                                <Button size="sm" variant="ghost" onClick={cancelEditingPrice} className="h-6 px-2">
                                  ✕
                                </Button>
                              </div>
                            ) : (
                              <div className="flex items-center gap-2">
                                <p className="text-sm text-muted-foreground">PKR {item.price.toLocaleString()} each</p>
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() => startEditingPrice(item.productId, item.price)}
                                  className="h-6 w-6 p-0"
                                >
                                  <Edit2 className="h-3 w-3" />
                                </Button>
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <Input
                            type="number"
                            min="1"
                            value={item.quantity}
                            onChange={(e) => updateItemQuantity(item.productId, Number.parseInt(e.target.value) || 0)}
                            className="w-20"
                          />
                          <span className="font-medium w-24 text-right">PKR {item.total.toLocaleString()}</span>
                          <Button variant="ghost" size="sm" onClick={() => removeItem(item.productId)}>
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Return Form */}
            {showReturnForm && (
              <Card className="border-orange-200 bg-orange-50/50">
                <CardHeader className="pb-4">
                  <CardTitle className="text-lg">Add Return Item</CardTitle>
                  <CardDescription>Select a product from previous sales to return</CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="space-y-2">
                    <Label>Search Previous Sales</Label>
                    <div className="relative">
                      <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                      <Input
                        value={returnProductSearch}
                        onChange={(e) => setReturnProductSearch(e.target.value)}
                        placeholder="Search products from previous sales..."
                        className="pl-10"
                      />
                    </div>
                    {returnProductSearch && (
                      <div className="mt-2 border rounded-lg max-h-40 overflow-y-auto">
                        {filteredPreviousItems.map((item, index) => (
                          <div
                            key={`${item.productId}-${index}`}
                            className="p-3 hover:bg-muted cursor-pointer border-b last:border-b-0"
                            onClick={() => {
                              setSelectedReturnProduct(item.productId)
                              setReturnProductPrice(item.price)
                              setReturnProductSearch("")
                            }}
                          >
                            <div className="flex justify-between items-center">
                              <div>
                                <p className="font-medium">{item.productName}</p>
                                <p className="text-sm text-muted-foreground">
                                  Receipt: {item.receiptId} • {item.customerName} •{" "}
                                  {new Date(item.receiptDate).toLocaleDateString()}
                                </p>
                              </div>
                              <span className="text-sm font-medium">PKR {item.price.toLocaleString()}</span>
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div className="space-y-2">
                      <Label>Selected Product</Label>
                      <Input
                        value={
                          selectedReturnProduct
                            ? allReceiptItems.find((p) => p.productId === selectedReturnProduct)?.productName || ""
                            : ""
                        }
                        placeholder="Search and select product above"
                        readOnly
                        className="bg-muted"
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Return Quantity</Label>
                      <Input
                        type="number"
                        min="1"
                        value={returnQuantity}
                        onChange={(e) => setReturnQuantity(Number.parseInt(e.target.value) || 1)}
                      />
                    </div>
                    <div className="space-y-2">
                      <Label>Return Price (PKR)</Label>
                      <Input
                        type="number"
                        min="0"
                        value={returnProductPrice}
                        onChange={(e) => setReturnProductPrice(Number.parseFloat(e.target.value) || 0)}
                        placeholder="Enter return price"
                      />
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Button onClick={handleAddReturn} size="sm">
                      Add Return
                    </Button>
                    <Button variant="outline" onClick={() => setShowReturnForm(false)} size="sm">
                      Cancel
                    </Button>
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Return Items Display */}
            {returnItems.length > 0 && (
              <div className="space-y-2">
                <Label>Return Items</Label>
                <div className="border rounded-lg border-orange-200 bg-orange-50/30">
                  {returnItems.map((returnItem, index) => (
                    <div key={`${returnItem.productId}-${index}`} className="p-3 border-b last:border-b-0">
                      <div className="flex items-center justify-between">
                        <div className="flex-1">
                          <p className="font-medium text-orange-800">{returnItem.productName}</p>
                          <div className="flex items-center gap-2">
                            {editingReturnPrice === returnItem.productId ? (
                              <div className="flex items-center gap-2">
                                <Input
                                  type="number"
                                  value={tempReturnPrice}
                                  onChange={(e) => setTempReturnPrice(e.target.value)}
                                  className="w-24 h-6 text-xs"
                                  placeholder="Price"
                                />
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() => saveReturnPrice(returnItem.productId, returnItem.quantity)}
                                  className="h-6 px-2"
                                >
                                  ✓
                                </Button>
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={cancelEditingReturnPrice}
                                  className="h-6 px-2"
                                >
                                  ✕
                                </Button>
                              </div>
                            ) : (
                              <div className="flex items-center gap-2">
                                <p className="text-sm text-orange-600">
                                  Qty: {returnItem.quantity} • Price: PKR
                                  {(returnItem.amount / returnItem.quantity).toLocaleString()}
                                </p>
                                <Button
                                  size="sm"
                                  variant="ghost"
                                  onClick={() =>
                                    startEditingReturnPrice(
                                      returnItem.productId,
                                      returnItem.amount,
                                      returnItem.quantity,
                                    )
                                  }
                                  className="h-6 w-6 p-0"
                                >
                                  <Edit2 className="h-3 w-3" />
                                </Button>
                              </div>
                            )}
                          </div>
                        </div>
                        <div className="flex items-center gap-2">
                          <span className="font-medium text-orange-800">-PKR {returnItem.amount.toLocaleString()}</span>
                          <Button
                            variant="ghost"
                            size="sm"
                            onClick={() => removeReturnItem(returnItem.productId)}
                            className="text-red-600 hover:text-red-700"
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Payment Details */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="discount">Discount (PKR)</Label>
              <Input
                id="discount"
                type="number"
                min="0"
                value={discount}
                onChange={(e) => setDiscount(Number.parseInt(e.target.value) || 0)}
                placeholder="Enter discount amount"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="amountReceived">Amount Received (PKR)</Label>
              <Input
                id="amountReceived"
                type="number"
                min="0"
                value={amountReceived}
                onChange={(e) => setAmountReceived(Number.parseFloat(e.target.value) || 0)}
                placeholder="Enter amount received"
              />
            </div>
          </div>

          {/* Notes */}
          <div className="space-y-2">
            <Label htmlFor="notes">Notes (Optional)</Label>
            <Textarea
              id="notes"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add any additional notes..."
              rows={3}
            />
          </div>

          {/* Summary */}
          {items.length > 0 && (
            <div className="border rounded-lg p-4 bg-muted/50">
              <h3 className="font-semibold mb-3">Receipt Summary</h3>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span>PKR {subtotal.toLocaleString()}</span>
                </div>
                <div className="flex justify-between">
                  <span>Discount:</span>
                  <span>-PKR {discount.toLocaleString()}</span>
                </div>
                {returnTotal > 0 && (
                  <div className="flex justify-between text-orange-600">
                    <span>Returns:</span>
                    <span>-PKR {returnTotal.toLocaleString()}</span>
                  </div>
                )}
                <div className="flex justify-between font-bold text-lg border-t pt-2">
                  <span>Total:</span>
                  <span>PKR {total.toLocaleString()}</span>
                </div>
                <div className="flex justify-between text-blue-600">
                  <span>Amount Received:</span>
                  <span>PKR {amountReceived.toLocaleString()}</span>
                </div>
                {pendingAmount > 0 && (
                  <div className="flex justify-between text-red-600 font-semibold">
                    <span>Pending Amount:</span>
                    <span>PKR {pendingAmount.toLocaleString()}</span>
                  </div>
                )}
              </div>
            </div>
          )}

          <div className="flex justify-end gap-2 pt-4 border-t">
            <Button variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button onClick={handleSubmit}>Create Receipt</Button>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

async function validateStockForReceipt(items: { productId: string; quantity: number }[]) {
  try {
    console.log("[v0] Validating stock for items:", items)

    const { getProducts } = await import("@/lib/database")
    const { syncManager } = await import("@/lib/sync-manager")

    // Get products from both sources
    const [dbProducts, syncProducts] = await Promise.all([getProducts(), syncManager.getProducts()])

    // Combine and deduplicate products
    const allProducts = [...(dbProducts || []), ...(syncProducts || [])]
    const products = allProducts.filter((product, index, self) => index === self.findIndex((p) => p.id === product.id))

    console.log("[v0] Available products for validation:", products.length)
    const invalidItems: any[] = []

    for (const item of items) {
      const product = products.find((p) => p.id.toString() === item.productId)
      console.log("[v0] Checking product:", item.productId, "found:", !!product, "stock:", product?.current_stock)

      if (!product || (product.current_stock || 0) < item.quantity) {
        invalidItems.push({
          productName: product?.name || "Unknown Product",
          requestedQuantity: item.quantity,
          availableStock: product?.current_stock || 0,
        })
      }
    }

    console.log("[v0] Stock validation result:", { isValid: invalidItems.length === 0, invalidItems })
    return {
      isValid: invalidItems.length === 0,
      invalidItems,
    }
  } catch (error) {
    console.error("[v0] Stock validation error:", error)
    return { isValid: false, invalidItems: [] }
  }
}

async function updateProductStock(productId: string, quantity: number) {
  try {
    const { createClient } = await import("@/lib/supabase/client")
    const supabase = createClient()

    // First check if product exists in database
    const { data: product } = await supabase
      .from("products")
      .select("id, name, current_stock")
      .eq("id", productId)
      .single()

    if (!product) {
      console.log(`[v0] Product ${productId} not found in database, skipping stock update`)
      return // Skip stock update for sync manager products
    }

    // Product exists in database, proceed with stock update
    const { updateProductStock: dbUpdateStock } = await import("@/lib/database")
    await dbUpdateStock(productId, quantity)
    console.log(`[v0] Updated stock for product ${productId}: -${quantity}`)
  } catch (error) {
    console.error(`[v0] Stock update error for product ${productId}:`, error)
    // Don't throw error to prevent receipt creation failure
    console.log(`[v0] Continuing receipt creation despite stock update failure`)
  }
}

components/modals/edit-product-modal.tsx.content:
"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Label } from "@/components/ui/label"
import { Textarea } from "@/components/ui/textarea"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Switch } from "@/components/ui/switch"
import { Plus, X } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { syncManager } from "@/lib/sync-manager"

interface EditProductModalProps {
  product: any
  onClose: () => void
  onEditProduct: (updatedProduct: any) => void
}

export function EditProductModal({ product, onClose, onEditProduct }: EditProductModalProps) {
  const [formData, setFormData] = useState({
    name: product.name || "",
    description: product.description || "",
    category: "", // Will be set after categories load
    cost_price: product.cost_price || 0,
    selling_price: product.selling_price || 0,
    current_stock: product.current_stock || 0,
    min_stock_level: product.min_stock_level || 0,
    max_stock_level: product.max_stock_level || 100,
    brand: product.brand || "",
    model: product.model || "",
    sku: product.sku || "",
    barcode: product.barcode || "",
    weight: product.weight || 0,
    dimensions: product.dimensions || "",
    color: product.color || "",
    size: product.size || "",
    material: product.material || "",
    warranty_period: product.warranty_period || "",
    supplier_info: product.supplier_info || "",
    images: product.images || [],
    tags: product.tags || [],
    featured: product.featured || false,
    active: product.active !== false,
  })

  const [newImageUrl, setNewImageUrl] = useState("")
  const [newTag, setNewTag] = useState("")
  const [isLoading, setIsLoading] = useState(false)
  const [categories, setCategories] = useState<any[]>([])
  const [loadingCategories, setLoadingCategories] = useState(true)
  const { toast } = useToast()

  useEffect(() => {
    const loadCategories = async () => {
      try {
        setLoadingCategories(true)
        const categoriesData = await syncManager.getCategories()
        setCategories(categoriesData || [])

        if (product.category_id && categoriesData) {
          const currentCategory = categoriesData.find((cat) => cat.id === product.category_id)
          if (currentCategory) {
            setFormData((prev) => ({ ...prev, category: currentCategory.name }))
          }
        }
      } catch (error) {
        console.error("[v0] Failed to load categories:", error)
        toast({
          title: "Error",
          description: "Failed to load categories. Please try again.",
          variant: "destructive",
        })
      } finally {
        setLoadingCategories(false)
      }
    }

    loadCategories()
  }, [toast, product.category_id])

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    setIsLoading(true)

    try {
      const selectedCategory = categories.find((cat) => cat.name === formData.category)
      if (!selectedCategory && formData.category) {
        toast({
          title: "Invalid Category",
          description: "Please select a valid category.",
          variant: "destructive",
        })
        setIsLoading(false)
        return
      }

      const updatedProduct = {
        ...product,
        ...formData,
        category_id: selectedCategory ? selectedCategory.id : product.category_id, // Use category_id instead of category
        image_url: formData.images[0] || product.image_url, // Set main image URL
        is_active: formData.active, // Map active to is_active
        updated_at: new Date().toISOString(),
      }

      delete updatedProduct.category

      onEditProduct(updatedProduct)

      toast({
        title: "Product Updated",
        description: `${formData.name} has been updated successfully.`,
      })

      onClose()
    } catch (error) {
      console.error("[v0] Failed to update product:", error)
      toast({
        title: "Error",
        description: "Failed to update product. Please try again.",
        variant: "destructive",
      })
    } finally {
      setIsLoading(false)
    }
  }

  const addImageUrl = () => {
    if (newImageUrl.trim()) {
      setFormData((prev) => ({
        ...prev,
        images: [...prev.images, newImageUrl.trim()],
      }))
      setNewImageUrl("")
    }
  }

  const removeImage = (index: number) => {
    setFormData((prev) => ({
      ...prev,
      images: prev.images.filter((_, i) => i !== index),
    }))
  }

  const addTag = () => {
    if (newTag.trim() && !formData.tags.includes(newTag.trim())) {
      setFormData((prev) => ({
        ...prev,
        tags: [...prev.tags, newTag.trim()],
      }))
      setNewTag("")
    }
  }

  const removeTag = (tagToRemove: string) => {
    setFormData((prev) => ({
      ...prev,
      tags: prev.tags.filter((tag) => tag !== tagToRemove),
    }))
  }

  return (
    <Dialog open={true} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[600px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>Edit Product</DialogTitle>
          <DialogDescription>Update the product information below.</DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Basic Information */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Basic Information</h3>

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="name">Product Name *</Label>
                <Input
                  id="name"
                  value={formData.name}
                  onChange={(e) => setFormData((prev) => ({ ...prev, name: e.target.value }))}
                  placeholder="Enter product name"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="category">Category *</Label>
                <Select
                  value={formData.category}
                  onValueChange={(value) => setFormData((prev) => ({ ...prev, category: value }))}
                >
                  <SelectTrigger>
                    <SelectValue placeholder={loadingCategories ? "Loading categories..." : "Select category"} />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((category) => (
                      <SelectItem key={category.id} value={category.name}>
                        {category.name}
                      </SelectItem>
                    ))}
                    {categories.length === 0 && !loadingCategories && (
                      <SelectItem value="no-categories" disabled>
                        No categories available
                      </SelectItem>
                    )}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div className="space-y-2">
              <Label htmlFor="description">Description</Label>
              <Textarea
                id="description"
                value={formData.description}
                onChange={(e) => setFormData((prev) => ({ ...prev, description: e.target.value }))}
                placeholder="Product description"
                rows={3}
              />
            </div>
          </div>

          {/* Pricing */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Pricing</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-2">
                <Label htmlFor="cost_price">Cost Price *</Label>
                <Input
                  id="cost_price"
                  type="number"
                  step="0.01"
                  value={formData.cost_price}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, cost_price: Number.parseFloat(e.target.value) || 0 }))
                  }
                  placeholder="0.00"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="selling_price">Selling Price *</Label>
                <Input
                  id="selling_price"
                  type="number"
                  step="0.01"
                  value={formData.selling_price}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, selling_price: Number.parseFloat(e.target.value) || 0 }))
                  }
                  placeholder="0.00"
                  required
                />
              </div>
            </div>
          </div>

          {/* Stock Management */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Stock Management</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="space-y-2">
                <Label htmlFor="current_stock">Current Stock *</Label>
                <Input
                  id="current_stock"
                  type="number"
                  value={formData.current_stock}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, current_stock: Number.parseInt(e.target.value) || 0 }))
                  }
                  placeholder="0"
                  required
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="min_stock_level">Min Stock Level</Label>
                <Input
                  id="min_stock_level"
                  type="number"
                  value={formData.min_stock_level}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, min_stock_level: Number.parseInt(e.target.value) || 0 }))
                  }
                  placeholder="0"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="max_stock_level">Max Stock Level</Label>
                <Input
                  id="max_stock_level"
                  type="number"
                  value={formData.max_stock_level}
                  onChange={(e) =>
                    setFormData((prev) => ({ ...prev, max_stock_level: Number.parseInt(e.target.value) || 100 }))
                  }
                  placeholder="100"
                />
              </div>
            </div>
          </div>

          {/* Product Images */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Product Images</h3>

            <div className="space-y-2">
              <Label>Add Image URL</Label>
              <div className="flex gap-2">
                <Input
                  value={newImageUrl}
                  onChange={(e) => setNewImageUrl(e.target.value)}
                  placeholder="Enter image URL"
                />
                <Button type="button" onClick={addImageUrl} disabled={!newImageUrl.trim()}>
                  <Plus className="h-4 w-4" />
                </Button>
              </div>
            </div>

            {formData.images.length > 0 && (
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
                {formData.images.map((imageUrl, index) => (
                  <div key={index} className="relative">
                    <img
                      src={imageUrl || "/placeholder.svg"}
                      alt={`Product ${index + 1}`}
                      className="w-full h-24 object-cover rounded-lg border"
                      onError={(e) => {
                        e.currentTarget.src = "/broken-image.png"
                      }}
                    />
                    <Button
                      type="button"
                      variant="destructive"
                      size="sm"
                      className="absolute top-1 right-1 h-6 w-6 p-0"
                      onClick={() => removeImage(index)}
                    >
                      <X className="h-3 w-3" />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Settings */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold">Settings</h3>
            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label htmlFor="featured">Featured Product</Label>
                <p className="text-sm text-muted-foreground">Display this product prominently</p>
              </div>
              <Switch
                id="featured"
                checked={formData.featured}
                onCheckedChange={(checked) => setFormData((prev) => ({ ...prev, featured: checked }))}
              />
            </div>

            <div className="flex items-center justify-between">
              <div className="space-y-0.5">
                <Label htmlFor="active">Active Product</Label>
                <p className="text-sm text-muted-foreground">Product is available for sale</p>
              </div>
              <Switch
                id="active"
                checked={formData.active}
                onCheckedChange={(checked) => setFormData((prev) => ({ ...prev, active: checked }))}
              />
            </div>
          </div>

          <div className="flex justify-end gap-3">
            <Button type="button" variant="outline" onClick={onClose}>
              Cancel
            </Button>
            <Button type="submit" disabled={isLoading || !formData.name.trim()}>
              {isLoading ? "Updating..." : "Update Product"}
            </Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  )
}

components/modals/receipt-detail-modal.t.content:
"use client"

import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Separator } from "@/components/ui/separator"
import { X, Download, Search } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { syncManager } from "@/lib/sync-manager"

interface ReceiptItem {
  productName: string
  quantity: number
  price: number
  total: number
}

interface ReturnItem {
  productId: number
  productName: string
  quantity: number
  amount: number
  originalPrice: number
}

interface Receipt {
  id: string
  customerName: string
  customerPhone: string
  amount: number
  receivedAmount?: number
  changeAmount?: number
  items: ReceiptItem[]
  returnItems?: ReturnItem[]
  subtotal: number
  discount: number
  returnTotal?: number
  tax: number
  total: number
  paymentMethod: string
  date: string
  status: string
  notes: string
}

interface ReceiptDetailModalProps {
  receipt: Receipt
  onClose: () => void
  onDelete?: (receiptId: string) => void // Add delete callback
}

export function ReceiptDetailModal({ receipt, onClose, onDelete }: ReceiptDetailModalProps) {
  const { toast } = useToast()

  const handleDownload = async () => {
    try {
      // Create a canvas element to render the receipt as an image
      const canvas = document.createElement("canvas")
      const ctx = canvas.getContext("2d")
      if (!ctx) return

      // Set canvas size
      canvas.width = 600
      canvas.height = 800

      // Set background
      ctx.fillStyle = "#ffffff"
      ctx.fillRect(0, 0, canvas.width, canvas.height)

      // Set text properties
      ctx.fillStyle = "#000000"
      ctx.font = "20px Arial"
      ctx.textAlign = "center"

      // Draw receipt content
      let y = 40
      ctx.fillText("HZ Shop", canvas.width / 2, y)
      y += 30
      ctx.font = "14px Arial"
      ctx.fillText("Professional Retail Management", canvas.width / 2, y)
      y += 40

      ctx.textAlign = "left"
      ctx.fillText(`Receipt #: ${receipt.id}`, 40, y)
      y += 25
      ctx.fillText(`Date: ${new Date(receipt.date).toLocaleString()}`, 40, y)
      y += 25
      ctx.fillText(`Customer: ${receipt.customerName}`, 40, y)
      y += 25
      ctx.fillText(`Phone: ${receipt.customerPhone}`, 40, y)
      y += 40

      // Draw items
      ctx.font = "bold 16px Arial"
      ctx.fillText("Items:", 40, y)
      y += 25
      ctx.font = "14px Arial"

      receipt.items.forEach((item) => {
        ctx.fillText(`${item.productName} x${item.quantity} @ ₹${item.price.toLocaleString()}`, 40, y)
        ctx.textAlign = "right"
        ctx.fillText(`₹${item.total.toLocaleString()}`, canvas.width - 40, y)
        ctx.textAlign = "left"
        y += 20
      })

      y += 20
      ctx.fillText(`Total: ₹${receipt.total.toLocaleString()}`, 40, y)
      if (receipt.receivedAmount) {
        y += 20
        ctx.fillText(`Received: ₹${receipt.receivedAmount.toLocaleString()}`, 40, y)
      }

      // Convert canvas to blob and download
      canvas.toBlob((blob) => {
        if (blob) {
          const url = URL.createObjectURL(blob)
          const link = document.createElement("a")
          link.href = url
          link.download = `HZ-Shop-Receipt-${receipt.id}.png`
          document.body.appendChild(link)
          link.click()
          document.body.removeChild(link)
          URL.revokeObjectURL(url)
        }
      }, "image/png")

      toast({
        title: "Receipt Downloaded",
        description: `Receipt ${receipt.id} has been downloaded as an image.`,
      })
    } catch (error) {
      console.error("Download failed:", error)
      toast({
        title: "Download Failed",
        description: "Failed to download receipt. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleShare = async () => {
    const shareText = generateReceiptText(receipt)

    if (navigator.share) {
      try {
        await navigator.share({
          title: `HZ Shop Receipt #${receipt.id}`,
          text: shareText,
        })
        toast({
          title: "Receipt Shared",
          description: "Receipt has been shared successfully.",
        })
      } catch (error) {
        // Fallback to clipboard
        await navigator.clipboard.writeText(shareText)
        toast({
          title: "Receipt Copied",
          description: "Receipt details copied to clipboard for sharing.",
        })
      }
    } else {
      // Fallback for browsers without Web Share API
      await navigator.clipboard.writeText(shareText)
      toast({
        title: "Receipt Copied",
        description: "Receipt details copied to clipboard for sharing.",
      })
    }
  }

  const handleDelete = async () => {
    if (window.confirm(`Are you sure you want to delete receipt #${receipt.id}? This action cannot be undone.`)) {
      try {
        await syncManager.deleteReceipt(receipt.id)
        onDelete?.(receipt.id)
        onClose()
        toast({
          title: "Receipt Deleted",
          description: `Receipt #${receipt.id} has been permanently deleted.`,
        })
      } catch (error) {
        console.error("Delete failed:", error)
        toast({
          title: "Delete Failed",
          description: "Failed to delete receipt. Please try again.",
          variant: "destructive",
        })
      }
    }
  }

  const generateReceiptText = (receipt: Receipt) => {
    return `
HZ SHOP RECEIPT
===============

Receipt #: ${receipt.id}
Date: ${new Date(receipt.date).toLocaleString()}
Customer: ${receipt.customerName}
Phone: ${receipt.customerPhone}

ITEMS:
------
${receipt.items
  .map(
    (item) =>
      `${item.productName} x${item.quantity} @ ₹${item.price.toLocaleString()} = ₹${item.total.toLocaleString()}`,
  )
  .join("\n")}

${
  receipt.returnItems && receipt.returnItems.length > 0
    ? `
RETURNS:
--------
${receipt.returnItems
  .map(
    (item) =>
      `${item.productName} x${item.quantity} @ ₹${(item.amount / item.quantity).toLocaleString()} = -₹${item.amount.toLocaleString()}`,
  )
  .join("\n")}
`
    : ""
}

SUMMARY:
--------
Subtotal: ₹${(receipt.subtotal || 0).toLocaleString()}
${(receipt.discount || 0) > 0 ? `Discount: -₹${(receipt.discount || 0).toLocaleString()}\n` : ""}${receipt.returnTotal && receipt.returnTotal > 0 ? `Returns: -₹${receipt.returnTotal.toLocaleString()}\n` : ""}Total: ₹${(receipt.total || 0).toLocaleString()}
${receipt.receivedAmount ? `Received: ₹${receipt.receivedAmount.toLocaleString()}\n` : ""}${receipt.changeAmount && receipt.changeAmount > 0 ? `Change: ₹${receipt.changeAmount.toLocaleString()}\n` : ""}
${receipt.notes ? `\nNotes: ${receipt.notes}\n` : ""}
===============
Thank you for your business!
HZ Shop - Your trusted retail partner
    `.trim()
  }

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-hidden">
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-4">
          <div>
            <CardTitle>Receipt #{receipt.id}</CardTitle>
            <CardDescription>{new Date(receipt.date).toLocaleString()}</CardDescription>
          </div>
          <div className="flex items-center gap-2">
            <Button variant="outline" size="sm" onClick={handleShare}>
              <Search className="h-4 w-4 mr-2" />
              Share
            </Button>
            <Button variant="outline" size="sm" onClick={handleDownload}>
              <Download className="h-4 w-4 mr-2" />
              Download
            </Button>
            {onDelete && (
              <Button
                variant="outline"
                size="sm"
                onClick={handleDelete}
                className="text-red-600 border-red-600 hover:bg-red-50 bg-transparent"
              >
                <X className="h-4 w-4 mr-2" />
                Delete
              </Button>
            )}
            <Button variant="ghost" size="sm" onClick={onClose}>
              <X className="h-4 w-4" />
            </Button>
          </div>
        </CardHeader>
        <CardContent className="overflow-y-auto max-h-[calc(90vh-120px)]">
          {/* Receipt Header */}
          <div className="text-center mb-6">
            <h2 className="text-2xl font-bold">HZ Shop</h2>
            <p className="text-muted-foreground">Professional Retail Management</p>
            <Separator className="my-4" />
          </div>

          {/* Customer Information */}
          <div className="mb-6">
            <h3 className="font-semibold mb-2">Customer Information</h3>
            <div className="space-y-1">
              <p>
                <strong>Name:</strong> {receipt.customerName}
              </p>
              <p>
                <strong>Phone:</strong> {receipt.customerPhone}
              </p>
              <p>
                <strong>Date:</strong> {new Date(receipt.date).toLocaleString()}
              </p>
              <p className="flex items-center gap-2">
                <strong>Status:</strong>
                <Badge variant={receipt.status === "paid" ? "default" : "outline"}>{receipt.status}</Badge>
              </p>
            </div>
          </div>

          {/* Items */}
          <div className="mb-6">
            <h3 className="font-semibold mb-2">Items</h3>
            <div className="border rounded-lg">
              <div className="grid grid-cols-4 gap-4 p-3 bg-muted font-medium text-sm">
                <span>Product</span>
                <span className="text-center">Qty</span>
                <span className="text-right">Price</span>
                <span className="text-right">Total</span>
              </div>
              {receipt.items.map((item, index) => (
                <div key={index} className="grid grid-cols-4 gap-4 p-3 border-t">
                  <span className="font-medium">{item.productName}</span>
                  <span className="text-center">{item.quantity}</span>
                  <span className="text-right">₹{item.price.toLocaleString()}</span>
                  <span className="text-right">₹{item.total.toLocaleString()}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Return Items */}
          {receipt.returnItems && receipt.returnItems.length > 0 && (
            <div className="mb-6">
              <h3 className="font-semibold mb-2 text-orange-600">Return Items</h3>
              <div className="border rounded-lg border-orange-200 bg-orange-50/30">
                <div className="grid grid-cols-4 gap-4 p-3 bg-orange-100 font-medium text-sm text-orange-800">
                  <span>Product</span>
                  <span className="text-center">Qty</span>
                  <span className="text-right">Price</span>
                  <span className="text-right">Total</span>
                </div>
                {receipt.returnItems.map((item, index) => (
                  <div key={index} className="grid grid-cols-4 gap-4 p-3 border-t border-orange-200">
                    <span className="font-medium text-orange-800">{item.productName}</span>
                    <span className="text-center text-orange-700">{item.quantity}</span>
                    <span className="text-right text-orange-700">
                      PKR {(item.amount / item.quantity).toLocaleString()}
                    </span>
                    <span className="text-right font-medium text-orange-800">-PKR {item.amount.toLocaleString()}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Payment Summary */}
          <div className="mb-6">
            <div className="border rounded-lg p-4 bg-muted/50">
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Subtotal:</span>
                  <span>PKR {(receipt.subtotal || 0).toLocaleString()}</span>
                </div>
                {(receipt.discount || 0) > 0 && (
                  <div className="flex justify-between">
                    <span>Discount:</span>
                    <span>-PKR {(receipt.discount || 0).toLocaleString()}</span>
                  </div>
                )}
                {receipt.returnTotal && receipt.returnTotal > 0 && (
                  <div className="flex justify-between text-orange-600">
                    <span>Returns:</span>
                    <span>-PKR {receipt.returnTotal.toLocaleString()}</span>
                  </div>
                )}
                {(receipt.tax || 0) > 0 && (
                  <div className="flex justify-between">
                    <span>Tax:</span>
                    <span>PKR {(receipt.tax || 0).toLocaleString()}</span>
                  </div>
                )}
                <Separator />
                <div className="flex justify-between font-bold text-lg">
                  <span>Total:</span>
                  <span>PKR {(receipt.total || 0).toLocaleString()}</span>
                </div>
                {receipt.receivedAmount && (
                  <>
                    <Separator />
                    <div className="flex justify-between">
                      <span>Received Amount:</span>
                      <span>PKR {receipt.receivedAmount.toLocaleString()}</span>
                    </div>
                    {receipt.changeAmount && receipt.changeAmount > 0 && (
                      <div className="flex justify-between">
                        <span>Change:</span>
                        <span>PKR {receipt.changeAmount.toLocaleString()}</span>
                      </div>
                    )}
                  </>
                )}
              </div>
            </div>
          </div>

          {/* Notes */}
          {receipt.notes && (
            <div className="mb-6">
              <h3 className="font-semibold mb-2">Notes</h3>
              <p className="text-sm text-muted-foreground p-3 bg-muted rounded-lg">{receipt.notes}</p>
            </div>
          )}

          {/* Footer */}
          <div className="text-center text-sm text-muted-foreground border-t pt-4">
            <p>Thank you for your business!</p>
            <p>HZ Shop - Your trusted retail partner</p>
          </div>
        </CardContent>
      </Card>
    </div>
  )
}

components/pages/categories.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Plus, Package, Trash2 } from "lucide-react"
import { AddCategoryModal } from "@/components/modals/add-category-modal"
import { useToast } from "@/hooks/use-toast"
import { getCategories, deleteCategory, getProducts, type Category } from "@/lib/database"
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog"

interface CategoriesProps {
  onNavigateToProducts?: (category: string) => void
}

export function Categories({ onNavigateToProducts }: CategoriesProps) {
  const [showAddModal, setShowAddModal] = useState(false)
  const [categories, setCategories] = useState<Category[]>([])
  const [loading, setLoading] = useState(true)
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false)
  const [categoryToDelete, setCategoryToDelete] = useState<Category | null>(null)
  const { toast } = useToast()

  const loadCategories = async () => {
    try {
      setLoading(true)
      const categoriesData = await getCategories()

      const categoriesWithCorrectCount = await Promise.all(
        categoriesData.map(async (category) => {
          const products = await getProducts(category.id)
          return {
            ...category,
            product_count: products.length,
          }
        }),
      )

      setCategories(categoriesWithCorrectCount)
    } catch (error) {
      console.error("[v0] Failed to load categories:", error)
      toast({
        title: "Error",
        description: "Failed to load categories. Please try again.",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    loadCategories()
  }, [])

  const handleViewProducts = async (categoryId: string, categoryName: string) => {
    try {
      const categoryProducts = await getProducts(categoryId)

      if (onNavigateToProducts) {
        onNavigateToProducts(categoryName)
        toast({
          title: "Navigating to Products",
          description: `Showing ${categoryProducts.length} products in ${categoryName} category`,
        })
      }
    } catch (error) {
      console.error("[v0] Failed to load category products:", error)
      toast({
        title: "Error",
        description: "Failed to load category products.",
        variant: "destructive",
      })
    }
  }

  const handleCategoryAdded = () => {
    setShowAddModal(false)
    loadCategories() // Reload categories after adding
    toast({
      title: "Category Added",
      description: "Category has been added successfully.",
    })
  }

  const handleDeleteClick = (category: Category) => {
    setCategoryToDelete(category)
    setDeleteDialogOpen(true)
  }

  const handleDeleteConfirm = async () => {
    if (!categoryToDelete) return

    try {
      await deleteCategory(categoryToDelete.id)
      toast({
        title: "Category Deleted",
        description: "Category has been deleted successfully.",
      })
      loadCategories() // Reload categories after deletion
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to delete category. Please try again.",
        variant: "destructive",
      })
    } finally {
      setDeleteDialogOpen(false)
      setCategoryToDelete(null)
    }
  }

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-2xl font-bold">Categories</h1>
            <p className="text-muted-foreground">Organize your products by categories</p>
          </div>
          <Button disabled>
            <Plus className="h-4 w-4 mr-2" />
            Add Category
          </Button>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[...Array(6)].map((_, i) => (
            <Card key={i}>
              <div className="animate-pulse">
                <div className="h-48 bg-muted rounded-t-lg"></div>
                <CardHeader>
                  <div className="h-6 bg-muted rounded mb-2"></div>
                  <div className="h-4 bg-muted rounded"></div>
                </CardHeader>
              </div>
            </Card>
          ))}
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold">Categories</h1>
          <p className="text-muted-foreground">Organize your products by categories</p>
        </div>
        <Button onClick={() => setShowAddModal(true)}>
          <Plus className="h-4 w-4 mr-2" />
          Add Category
        </Button>
      </div>

      {/* Categories Grid */}
      {categories.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {categories.map((category) => (
            <Card key={category.id} className="hover:shadow-md transition-shadow cursor-pointer group">
              <div className="relative">
                <img
                  src={category.image_url || "/placeholder.svg?height=200&width=300&query=category"}
                  alt={category.name}
                  className="w-full h-48 object-cover rounded-t-lg bg-muted"
                />
                <Button
                  size="sm"
                  variant="destructive"
                  className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity h-8 w-8 p-0"
                  onClick={(e) => {
                    e.stopPropagation()
                    handleDeleteClick(category)
                  }}
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  {category.name}
                  <Badge variant="outline">
                    <Package className="h-3 w-3 mr-1" />
                    {category.product_count || 0}
                  </Badge>
                </CardTitle>
                <CardDescription>{category.description || "No description"}</CardDescription>
              </CardHeader>
              <CardContent>
                <Button
                  variant="outline"
                  className="w-full bg-transparent"
                  onClick={() => handleViewProducts(category.id, category.name)}
                >
                  View Products
                </Button>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="flex flex-col items-center justify-center py-12">
            <Package className="h-12 w-12 text-muted-foreground mb-4" />
            <h3 className="text-lg font-semibold mb-2">No Categories Found</h3>
            <p className="text-muted-foreground text-center mb-4">
              Get started by creating your first product category.
            </p>
            <Button onClick={() => setShowAddModal(true)}>
              <Plus className="h-4 w-4 mr-2" />
              Add Category
            </Button>
          </CardContent>
        </Card>
      )}

      {showAddModal && (
        <AddCategoryModal onClose={() => setShowAddModal(false)} onCategoryAdded={handleCategoryAdded} />
      )}

      <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Category</AlertDialogTitle>
            <AlertDialogDescription>
              Are you sure you want to delete "{categoryToDelete?.name}"? This action cannot be undone.
              {categoryToDelete?.product_count && categoryToDelete.product_count > 0 && (
                <span className="block mt-2 text-destructive font-medium">
                  This category has {categoryToDelete.product_count} products and cannot be deleted.
                </span>
              )}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={handleDeleteConfirm}
              disabled={categoryToDelete?.product_count && categoryToDelete.product_count > 0}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              Delete
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </div>
  )
}

components/pages/products.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Badge } from "@/components/ui/badge"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Checkbox } from "@/components/ui/checkbox"
import { ProductDetailModal } from "@/components/modals/product-detail-modal"
import { AddProductModal } from "@/components/modals/add-product-modal"
import { EditProductModal } from "@/components/modals/edit-product-modal"
import { Search, Filter, Plus, Eye, Edit, Trash2, Grid, List, X } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { syncManager } from "@/lib/sync-manager"
import { getCategories } from "@/lib/database"

const sortOptions = [
  { value: "name-asc", label: "Name (A-Z)" },
  { value: "name-desc", label: "Name (Z-A)" },
  { value: "price-asc", label: "Price (Low to High)" },
  { value: "price-desc", label: "Price (High to Low)" },
  { value: "stock-asc", label: "Stock (Low to High)" },
  { value: "stock-desc", label: "Stock (High to Low)" },
]

interface ProductsProps {
  selectedCategory?: string | null
  onClearCategory?: () => void
}

export function Products({ selectedCategory, onClearCategory }: ProductsProps) {
  const [products, setProducts] = useState<any[]>([])
  const [categories, setCategories] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [categoryFilter, setCategoryFilter] = useState("All")
  const [sortBy, setSortBy] = useState("name-asc")
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid")
  const [selectedProducts, setSelectedProducts] = useState<string[]>([])
  const [selectedProduct, setSelectedProduct] = useState<any | null>(null)
  const [showAddModal, setShowAddModal] = useState(false)
  const [showFilters, setShowFilters] = useState(false)
  const [editingProduct, setEditingProduct] = useState<any | null>(null)
  const { toast } = useToast()

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true)
        const [productsData, categoriesData] = await Promise.all([syncManager.getProducts(), getCategories()])

        console.log("[v0] Loaded products from sync manager:", productsData.length)
        setProducts(productsData)

        setCategories([{ id: "all", name: "All" }, ...categoriesData])
      } catch (error) {
        console.error("[v0] Failed to load data:", error)
        toast({
          title: "Error",
          description: "Failed to load data. Please try again.",
          variant: "destructive",
        })
      } finally {
        setLoading(false)
      }
    }

    loadData()
  }, [toast])

  useEffect(() => {
    if (selectedCategory) {
      setCategoryFilter(selectedCategory)
      setSearchTerm("") // Clear search when navigating from categories
    }
  }, [selectedCategory])

  const handleAddProduct = async (newProduct: any) => {
    try {
      const productWithId = {
        ...newProduct,
        id: `product_${Date.now()}`,
        created_at: new Date().toISOString(),
        updated_at: new Date().toISOString(),
      }

      await syncManager.saveProduct(productWithId)
      setProducts((prev) => [...prev, productWithId])

      toast({
        title: "Product Added",
        description: `${newProduct.name} has been added successfully.`,
      })

      console.log("[v0] Product added and synced:", productWithId.name)
    } catch (error) {
      console.error("[v0] Failed to add product:", error)
      toast({
        title: "Error",
        description: "Failed to add product. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleEditProduct = (product: any) => {
    setEditingProduct(product)
  }

  const handleUpdateProduct = async (updatedProduct: any) => {
    try {
      await syncManager.saveProduct(updatedProduct)
      setProducts((prev) => prev.map((p) => (p.id === updatedProduct.id ? updatedProduct : p)))

      toast({
        title: "Product Updated",
        description: `${updatedProduct.name} has been updated successfully.`,
      })
    } catch (error) {
      console.error("[v0] Failed to update product:", error)
      toast({
        title: "Error",
        description: "Failed to update product. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleDeleteProduct = async (productId: string) => {
    try {
      await syncManager.deleteProduct(productId)
      setProducts((prev) => prev.filter((p) => p.id !== productId))

      toast({
        title: "Product Deleted",
        description: "Product has been permanently removed from inventory.",
      })
    } catch (error) {
      console.error("[v0] Failed to delete product:", error)
      toast({
        title: "Error",
        description: "Failed to delete product. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleSelectProduct = (productId: string) => {
    setSelectedProducts((prev) =>
      prev.includes(productId) ? prev.filter((id) => id !== productId) : [...prev, productId],
    )
  }

  const handleSelectAll = () => {
    if (selectedProducts.length === products.length) {
      setSelectedProducts([])
    } else {
      setSelectedProducts(products.map((p) => p.id))
    }
  }

  const handleBulkDelete = async () => {
    try {
      // Bulk delete would need to be implemented in sync manager
      toast({
        title: "Products Deleted",
        description: `${selectedProducts.length} products have been deleted.`,
      })
      setSelectedProducts([])
    } catch (error) {
      console.error("[v0] Failed to delete products:", error)
      toast({
        title: "Error",
        description: "Failed to delete products. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleBulkStatusChange = (status: string) => {
    toast({
      title: "Status Updated",
      description: `${selectedProducts.length} products marked as ${status}.`,
    })
    setSelectedProducts([])
  }

  const handleClearCategoryFilter = () => {
    setCategoryFilter("All")
    if (onClearCategory) {
      onClearCategory()
    }
  }

  const filteredProducts = products
    .filter((product) => {
      const matchesSearch =
        product.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
        categories
          .find((c) => c.id === product.category_id)
          ?.name?.toLowerCase()
          .includes(searchTerm.toLowerCase()) ||
        product.description?.toLowerCase().includes(searchTerm.toLowerCase())

      const matchesCategory =
        categoryFilter === "All" || product.category_id === categories.find((c) => c.name === categoryFilter)?.id

      return matchesSearch && matchesCategory
    })
    .sort((a, b) => {
      switch (sortBy) {
        case "name-asc":
          return (a.name || "").localeCompare(b.name || "")
        case "name-desc":
          return (b.name || "").localeCompare(a.name || "")
        case "price-asc":
          return (a.selling_price || 0) - (b.selling_price || 0)
        case "price-desc":
          return (b.selling_price || 0) - (a.selling_price || 0)
        case "stock-asc":
          return (a.current_stock || 0) - (b.current_stock || 0)
        case "stock-desc":
          return (b.current_stock || 0) - (a.current_stock || 0)
        default:
          return 0
      }
    })

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold">Products</h1>
          <p className="text-muted-foreground">
            Manage your inventory and product catalog ({filteredProducts.length} products)
            {selectedCategory && selectedCategory !== "All" && (
              <span className="ml-2">
                - Filtered by <strong>{selectedCategory}</strong>
              </span>
            )}
          </p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={() => setViewMode(viewMode === "grid" ? "list" : "grid")}>
            {viewMode === "grid" ? <List className="h-4 w-4" /> : <Grid className="h-4 w-4" />}
          </Button>
          <Button onClick={() => setShowAddModal(true)}>
            <Plus className="h-4 w-4 mr-2" />
            Add Product
          </Button>
        </div>
      </div>

      {selectedCategory && selectedCategory !== "All" && (
        <div className="flex items-center gap-2">
          <Badge variant="secondary" className="flex items-center gap-2">
            Filtered by: {selectedCategory}
            <Button
              variant="ghost"
              size="sm"
              className="h-4 w-4 p-0 hover:bg-transparent"
              onClick={handleClearCategoryFilter}
            >
              <X className="h-3 w-3" />
            </Button>
          </Badge>
        </div>
      )}

      {/* Search and Filters */}
      <Card>
        <CardContent className="pt-6">
          <div className="space-y-4">
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Search products by name, category, or description..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
              <div className="flex gap-2">
                <Select value={categoryFilter} onValueChange={setCategoryFilter}>
                  <SelectTrigger className="w-40">
                    <SelectValue placeholder="Category" />
                  </SelectTrigger>
                  <SelectContent>
                    {categories.map((category) => (
                      <SelectItem key={category.id} value={category.name}>
                        {category.name}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Select value={sortBy} onValueChange={setSortBy}>
                  <SelectTrigger className="w-48">
                    <SelectValue placeholder="Sort by" />
                  </SelectTrigger>
                  <SelectContent>
                    {sortOptions.map((option) => (
                      <SelectItem key={option.value} value={option.value}>
                        {option.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Button variant="outline" onClick={() => setShowFilters(!showFilters)}>
                  <Filter className="h-4 w-4 mr-2" />
                  Filters
                </Button>
              </div>
            </div>

            {/* Advanced Filters */}
            {showFilters && (
              <div className="border-t pt-4">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div>
                    <label className="text-sm font-medium mb-2 block">Stock Status</label>
                    <div className="space-y-2">
                      <div className="flex items-center space-x-2">
                        <Checkbox id="in-stock" />
                        <label htmlFor="in-stock" className="text-sm">
                          In Stock
                        </label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Checkbox id="low-stock" />
                        <label htmlFor="low-stock" className="text-sm">
                          Low Stock
                        </label>
                      </div>
                      <div className="flex items-center space-x-2">
                        <Checkbox id="out-of-stock" />
                        <label htmlFor="out-of-stock" className="text-sm">
                          Out of Stock
                        </label>
                      </div>
                    </div>
                  </div>
                  <div>
                    <label className="text-sm font-medium mb-2 block">Price Range</label>
                    <div className="space-y-2">
                      <Input placeholder="Min price" type="number" />
                      <Input placeholder="Max price" type="number" />
                    </div>
                  </div>
                  <div>
                    <label className="text-sm font-medium mb-2 block">Brand</label>
                    <Select>
                      <SelectTrigger>
                        <SelectValue placeholder="Select brand" />
                      </SelectTrigger>
                      <SelectContent>
                        <SelectItem value="apple">Apple</SelectItem>
                        <SelectItem value="samsung">Samsung</SelectItem>
                        <SelectItem value="google">Google</SelectItem>
                      </SelectContent>
                    </Select>
                  </div>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Bulk Actions */}
      {selectedProducts.length > 0 && (
        <Card>
          <CardContent className="pt-6">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-4">
                <Checkbox checked={selectedProducts.length === products.length} onCheckedChange={handleSelectAll} />
                <span className="text-sm font-medium">{selectedProducts.length} products selected</span>
              </div>
              <div className="flex gap-2">
                <Button variant="outline" size="sm" onClick={() => handleBulkStatusChange("active")}>
                  Mark Active
                </Button>
                <Button variant="outline" size="sm" onClick={() => handleBulkStatusChange("inactive")}>
                  Mark Inactive
                </Button>
                <Button variant="destructive" size="sm" onClick={handleBulkDelete}>
                  <Trash2 className="h-4 w-4 mr-2" />
                  Delete Selected
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Products Display */}
      {viewMode === "grid" ? (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {filteredProducts.map((product) => (
            <Card key={product.id} className="hover:shadow-md transition-shadow">
              <CardContent className="p-4">
                <div className="flex items-start gap-4">
                  <Checkbox
                    checked={selectedProducts.includes(product.id)}
                    onCheckedChange={() => handleSelectProduct(product.id)}
                    className="mt-1"
                  />
                  <img
                    src={product.image_url || "/placeholder.svg?height=64&width=64"}
                    alt={product.name}
                    className="w-16 h-16 rounded-lg object-cover bg-muted cursor-pointer"
                    onClick={() => setSelectedProduct(product)}
                  />
                  <div className="flex-1 min-w-0">
                    <h3 className="font-semibold truncate">{product.name}</h3>
                    <p className="text-sm text-muted-foreground">
                      {categories.find((c) => c.id === product.category_id)?.name || "No Category"}
                    </p>
                    <div className="flex items-center gap-2 mt-2">
                      <Badge
                        variant={
                          product.current_stock > 0
                            ? product.current_stock <= (product.min_stock_level || 0)
                              ? "outline"
                              : "default"
                            : "destructive"
                        }
                      >
                        {product.current_stock > 0
                          ? product.current_stock <= (product.min_stock_level || 0)
                            ? `Low: ${product.current_stock}`
                            : `${product.current_stock} in stock`
                          : "Out of stock"}
                      </Badge>
                    </div>
                    <div className="mt-2">
                      <p className="text-sm">
                        <span className="text-muted-foreground">Sale:</span> PKR{" "}
                        {(product.selling_price || 0).toLocaleString()}
                      </p>
                      <p className="text-sm">
                        <span className="text-muted-foreground">Cost:</span> PKR{" "}
                        {(product.cost_price || 0).toLocaleString()}
                      </p>
                      <p className="text-sm">
                        <span className="text-muted-foreground">Profit:</span> PKR{" "}
                        {((product.selling_price || 0) - (product.cost_price || 0)).toLocaleString()}
                      </p>
                    </div>
                  </div>
                </div>
                <div className="flex justify-end gap-2 mt-4">
                  <Button variant="ghost" size="sm" onClick={() => setSelectedProduct(product)}>
                    <Eye className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost" size="sm" onClick={() => handleEditProduct(product)}>
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button variant="ghost" size="sm" onClick={() => handleDeleteProduct(product.id)}>
                    <Trash2 className="h-4 w-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      ) : (
        <Card>
          <CardContent className="p-0">
            <div className="divide-y">
              {filteredProducts.map((product) => (
                <div key={product.id} className="p-4 hover:bg-muted/50">
                  <div className="flex items-center gap-4">
                    <Checkbox
                      checked={selectedProducts.includes(product.id)}
                      onCheckedChange={() => handleSelectProduct(product.id)}
                    />
                    <img
                      src={product.image_url || "/placeholder.svg?height=48&width=48"}
                      alt={product.name}
                      className="w-12 h-12 rounded object-cover bg-muted cursor-pointer"
                      onClick={() => setSelectedProduct(product)}
                    />
                    <div className="flex-1 grid grid-cols-1 md:grid-cols-6 gap-4 items-center">
                      <div className="md:col-span-2">
                        <h3 className="font-semibold">{product.name}</h3>
                        <p className="text-sm text-muted-foreground">
                          {categories.find((c) => c.id === product.category_id)?.name || "No Category"}
                        </p>
                      </div>
                      <div>
                        <p className="text-sm">PKR {(product.selling_price || 0).toLocaleString()}</p>
                        <p className="text-xs text-muted-foreground">Sale Price</p>
                      </div>
                      <div>
                        <Badge
                          variant={
                            product.current_stock > 0
                              ? product.current_stock <= (product.min_stock_level || 0)
                                ? "outline"
                                : "default"
                              : "destructive"
                          }
                        >
                          {product.current_stock > 0
                            ? product.current_stock <= (product.min_stock_level || 0)
                              ? `Low: ${product.current_stock}`
                              : `${product.current_stock} units`
                            : "Out of stock"}
                        </Badge>
                      </div>
                      <div>
                        <p className="text-sm font-medium">
                          PKR {((product.selling_price || 0) - (product.cost_price || 0)).toLocaleString()}
                        </p>
                        <p className="text-xs text-muted-foreground">Profit</p>
                      </div>
                      <div className="flex justify-end gap-2">
                        <Button variant="ghost" size="sm" onClick={() => setSelectedProduct(product)}>
                          <Eye className="h-4 w-4" />
                        </Button>
                        <Button variant="ghost" size="sm" onClick={() => handleEditProduct(product)}>
                          <Edit className="h-4 w-4" />
                        </Button>
                        <Button variant="ghost" size="sm" onClick={() => handleDeleteProduct(product.id)}>
                          <Trash2 className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Show empty state if no products */}
      {filteredProducts.length === 0 && !loading && (
        <Card>
          <CardContent className="py-12 text-center">
            <div className="text-muted-foreground">
              <h3 className="text-lg font-medium mb-2">No products found</h3>
              <p className="mb-4">
                {searchTerm || categoryFilter !== "All"
                  ? "Try adjusting your search or filters"
                  : "Start by adding your first product"}
              </p>
              <Button onClick={() => setShowAddModal(true)}>
                <Plus className="h-4 w-4 mr-2" />
                Add Product
              </Button>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Modals */}
      {selectedProduct && (
        <ProductDetailModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onEdit={handleEditProduct}
          onDelete={handleDeleteProduct}
        />
      )}
      {showAddModal && <AddProductModal onClose={() => setShowAddModal(false)} onAddProduct={handleAddProduct} />}
      {editingProduct && (
        <EditProductModal
          product={editingProduct}
          onClose={() => setEditingProduct(null)}
          onEditProduct={handleUpdateProduct}
        />
      )}
    </div>
  )
}

components/pages/public-catalog.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import {
  Share2,
  RefreshCw,
  Eye,
  Search,
  Grid,
  List,
  ShoppingCart,
  Heart,
  Plus,
  Minus,
  X,
  MessageCircle,
  Receipt,
} from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import {
  getProducts,
  getCategories,
  addToCart,
  getCartItems,
  updateCartItemQuantity,
  removeFromCart,
} from "@/lib/database"
import { CreateReceiptModal } from "@/components/modals/create-receipt-modal"

export function PublicCatalog() {
  const [products, setProducts] = useState<any[]>([])
  const [categories, setCategories] = useState<any[]>([])
  const [cartItems, setCartItems] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [syncManager, setSyncManager] = useState<any>(null)
  const [searchQuery, setSearchQuery] = useState("")
  const [selectedCategory, setSelectedCategory] = useState("all")
  const [sortBy, setSortBy] = useState("featured")
  const [viewMode, setViewMode] = useState<"grid" | "list">("grid")
  const [selectedProduct, setSelectedProduct] = useState<any>(null)
  const [showCart, setShowCart] = useState(false)
  const [showReceiptModal, setShowReceiptModal] = useState(false)
  const [sessionId] = useState(() => {
    // Try to get existing sessionId from localStorage, or create new one
    if (typeof window !== 'undefined') {
      const stored = localStorage.getItem('cart_session_id')
      if (stored) {
        console.log("[v0] Using existing cart session:", stored)
        return stored
      }
    }
    const newSession = `session_${Date.now()}_${Math.random()}`
    if (typeof window !== 'undefined') {
      localStorage.setItem('cart_session_id', newSession)
      console.log("[v0] Created new cart session:", newSession)
    }
    return newSession
  })

  const { toast } = useToast()

  useEffect(() => {
    const loadSyncManager = async () => {
      try {
        const { syncManager: sm } = await import("@/lib/sync-manager")
        setSyncManager(sm)
        console.log("[v0] SyncManager loaded successfully")
      } catch (error) {
        console.error("[v0] Error loading syncManager:", error)
        setSyncManager(null)
      }
    }
    loadSyncManager()
  }, [])

  useEffect(() => {
    if (syncManager !== undefined) {
      loadData()
    }
  }, [syncManager])

  const loadData = async () => {
    try {
      setLoading(true)
      console.log("[v0] Loading catalog data...")

      const syncProducts = syncManager ? await syncManager.getProducts() : []

      const [dbProducts, dbCategories, cartData] = await Promise.all([
        getProducts(),
        getCategories(),
        getCartItems(sessionId),
      ])

      // For public catalog, use database as source of truth to avoid phantom products
      // Only use sync products as fallback when database fails or offline
      let uniqueProducts = dbProducts || []

      // Only merge sync products if they exist in database (verified by ID)
      if (syncProducts && syncProducts.length > 0 && dbProducts && dbProducts.length > 0) {
        const dbIds = new Set(dbProducts.map(p => p.id))
        const validSyncProducts = syncProducts.filter((p: any) => 
          p.id && 
          dbIds.has(p.id) &&
          p.is_active !== false &&
          typeof p.current_stock === 'number' && 
          p.current_stock >= 0
        )
        uniqueProducts = [...dbProducts, ...validSyncProducts].filter(
          (product, index, self) => index === self.findIndex((p) => p.id === product.id),
        )
      }

      // Final validation: only show products with valid UUID IDs and active status
      uniqueProducts = uniqueProducts.filter(product => 
        product.id && 
        typeof product.id === 'string' && 
        product.id.length > 10 && // Basic UUID length check
        product.is_active !== false &&
        typeof product.current_stock === 'number' &&
        product.current_stock >= 0
      )

      console.log("[v0] Loaded products from database:", dbProducts?.length || 0)
      console.log("[v0] Loaded products from sync manager:", syncProducts?.length || 0)
      console.log("[v0] Total unique products:", uniqueProducts.length)
      console.log("[v0] Loaded categories from database:", dbCategories?.length || 0)

      setProducts(uniqueProducts)
      setCategories(dbCategories || [])
      setCartItems(cartData || [])
    } catch (error) {
      console.error("[v0] Error loading catalog data:", error)
      toast({
        title: "Error",
        description: "Failed to load catalog data",
        variant: "destructive",
      })
    } finally {
      setLoading(false)
    }
  }

  const filteredProducts = products
    .filter((product) => {
      const matchesSearch =
        (product.name || "").toLowerCase().includes(searchQuery.toLowerCase()) ||
        (product.description || "").toLowerCase().includes(searchQuery.toLowerCase())

      const matchesCategory =
        selectedCategory === "all" || product.category_id === selectedCategory || product.category === selectedCategory

      const isActive = product.is_active !== false && product.status !== "inactive"

      return matchesSearch && matchesCategory && isActive
    })
    .sort((a, b) => {
      switch (sortBy) {
        case "name":
          return (a.name || "").localeCompare(b.name || "")
        case "stock-high":
          return (b.current_stock || 0) - (a.current_stock || 0)
        case "stock-low":
          return (a.current_stock || 0) - (b.current_stock || 0)
        default:
          return 0
      }
    })

  const handleShareCatalog = () => {
    toast({
      title: "Catalog Shared",
      description: "Public catalog link has been copied to clipboard.",
    })
  }

  const handleUpdateCatalog = () => {
    console.log("[v0] Updating catalog...")
    loadData()
    toast({
      title: "Catalog Updated",
      description: "Public catalog has been refreshed with latest products.",
    })
  }

  const handleAddToCart = async (product: any) => {
    try {
      console.log("[v0] Adding to cart:", product.name)

      const currentStock = Number(product.current_stock) || 0

      if (currentStock <= 0) {
        toast({
          title: "Out of Stock",
          description: `${product.name} is currently out of stock.`,
          variant: "destructive",
        })
        return
      }

      if (!product.id) {
        toast({
          title: "Error",
          description: "Product ID is missing. Please refresh the catalog.",
          variant: "destructive",
        })
        return
      }

      const existingCartItem = cartItems.find((item) => item.product_id === product.id)
      const currentCartQuantity = existingCartItem ? existingCartItem.quantity : 0

      if (currentStock <= currentCartQuantity) {
        toast({
          title: "Cannot Add More",
          description: `You already have ${currentCartQuantity} ${product.name} in your cart. Only ${currentStock} available.`,
          variant: "destructive",
        })
        return
      }

      await addToCart(sessionId, product.id, 1)

      const updatedCartItems = await getCartItems(sessionId)
      setCartItems(updatedCartItems || [])

      const newQuantity = currentCartQuantity + 1
      const newCartTotal = (updatedCartItems || []).reduce((sum, item) => sum + item.quantity, 0)

      toast({
        title: "Added to Cart! 🛒",
        description: `${product.name} (${newQuantity} ${newQuantity === 1 ? "item" : "items"}) • Total cart: ${newCartTotal} items`,
        duration: 4000,
      })
    } catch (error: any) {
      console.error("[v0] Error adding to cart:", error)

      let errorMessage = "Failed to add item to cart"
      if (error.message?.includes("Insufficient stock")) {
        errorMessage = `${product.name} is out of stock or has insufficient quantity available.`
      } else if (error.message?.includes("Product not found")) {
        errorMessage = `${product.name} is not available in the database. Please refresh the catalog.`
      } else if (error.message) {
        errorMessage = error.message
      }

      toast({
        title: "Cannot Add to Cart",
        description: errorMessage,
        variant: "destructive",
      })
    }
  }

  const handleAddToWishlist = (product: any) => {
    toast({
      title: "Added to Wishlist",
      description: `${product.name} has been saved to your wishlist.`,
    })
  }

  const updateCartQuantity = async (cartItemId: string, newQuantity: number) => {
    try {
      await updateCartItemQuantity(cartItemId, newQuantity)

      const updatedCartItems = await getCartItems(sessionId)
      setCartItems(updatedCartItems || [])

      toast({
        title: "Cart Updated",
        description: newQuantity === 0 ? "Item removed from cart" : `Quantity updated to ${newQuantity}`,
        duration: 2000,
      })
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to update cart",
        variant: "destructive",
      })
    }
  }

  const removeCartItem = async (cartItemId: string) => {
    try {
      const item = cartItems.find((cartItem) => cartItem.id === cartItemId)
      const itemName = item?.product?.name || "Item"

      await removeFromCart(cartItemId)

      const updatedCartItems = await getCartItems(sessionId)
      setCartItems(updatedCartItems || [])

      toast({
        title: "Removed from Cart",
        description: `${itemName} has been removed from your cart`,
        duration: 2000,
      })
    } catch (error: any) {
      toast({
        title: "Error",
        description: error.message || "Failed to remove item",
        variant: "destructive",
      })
    }
  }

  const shareCartViaWhatsApp = () => {
    if (cartItems.length === 0) {
      toast({
        title: "Empty Cart",
        description: "Add some items to your cart before sharing.",
        variant: "destructive",
      })
      return
    }

    const cartSummary = cartItems
      .map((item) => {
        const price = item.product?.selling_price || 0
        const total = price * item.quantity
        return `• ${item.product?.name} (Qty: ${item.quantity}) - PKR ${total.toLocaleString()}`
      })
      .join("\n")

    const message = `🛒 *My Cart from HZ Shop*\n\n${cartSummary}\n\n*Total Items:* ${cartItemCount}\n*Cart Total:* PKR ${cartTotal.toLocaleString()}\n\nI'd like to place this order. Please confirm availability and total amount.\n\nShared from HZ Shop Catalog`

    const whatsappUrl = `https://wa.me/?text=${encodeURIComponent(message)}`
    window.open(whatsappUrl, "_blank")

    toast({
      title: "Shared via WhatsApp! 📱",
      description: "Your cart details have been shared successfully.",
      duration: 3000,
    })
  }

  const handlePayNow = () => {
    if (cartItems.length === 0) {
      toast({
        title: "Empty Cart",
        description: "Add some items to your cart before checkout.",
        variant: "destructive",
      })
      return
    }

    toast({
      title: "Opening Checkout",
      description: `Processing ${cartItemCount} items for receipt creation...`,
      duration: 2000,
    })

    setShowReceiptModal(true)
    setShowCart(false)
  }

  const cartTotal = cartItems.reduce((sum, item) => sum + item.quantity * (item.product?.selling_price || 0), 0)
  const cartItemCount = cartItems.reduce((sum, item) => sum + item.quantity, 0)

  const ProductDetailModal = ({ product, onClose }: { product: any; onClose: () => void }) => (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
      <Card className="w-full max-w-2xl max-h-[90vh] overflow-hidden">
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <span>{product.name || "Unnamed Product"}</span>
            <Button variant="ghost" size="sm" onClick={onClose}>
              ×
            </Button>
          </CardTitle>
        </CardHeader>
        <CardContent className="overflow-y-auto space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <img
                src={product.image_url || "/placeholder.svg?height=300&width=300"}
                alt={product.name || "Product"}
                className="w-full h-64 object-cover rounded-lg"
              />
              {product.youtube_url && (
                <div className="mt-4">
                  <h4 className="font-medium mb-2">Product Video</h4>
                  <div className="aspect-video">
                    <iframe
                      src={product.youtube_url.replace("watch?v=", "embed/")}
                      className="w-full h-full rounded-lg"
                      allowFullScreen
                    />
                  </div>
                </div>
              )}
            </div>
            <div className="space-y-4">
              <div>
                <h3 className="font-semibold text-lg mb-2">Stock Availability</h3>
                <Badge variant={product.current_stock > 0 ? "default" : "destructive"} className="text-base px-3 py-1">
                  {product.current_stock > 0 ? `${product.current_stock || 0} units available` : "Out of Stock"}
                </Badge>
              </div>

              <div>
                <h4 className="font-medium mb-2">Status</h4>
                <Badge variant={product.is_active ? "default" : "secondary"}>
                  {product.is_active ? "Active" : "Inactive"}
                </Badge>
              </div>

              {product.specifications && (
                <div>
                  <h4 className="font-medium mb-2">Specifications</h4>
                  <p className="text-sm text-muted-foreground">{String(product.specifications)}</p>
                </div>
              )}

              {product.description && (
                <div>
                  <h4 className="font-medium mb-2">Description</h4>
                  <p className="text-sm text-muted-foreground">{String(product.description)}</p>
                </div>
              )}

              <Button className="w-full" onClick={() => handleAddToCart(product)} disabled={product.current_stock <= 0}>
                <ShoppingCart className="h-4 w-4 mr-2" />
                {product.current_stock > 0 ? "Add to Cart" : "Out of Stock"}
              </Button>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  )

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-2"></div>
          <p>Loading catalog...</p>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold">Public Catalog Preview</h1>
          <p className="text-muted-foreground">Preview how customers see your product catalog</p>
        </div>
        <div className="flex gap-2">
          <Button variant="outline" onClick={handleShareCatalog}>
            <Share2 className="h-4 w-4 mr-2" />
            Share Catalog
          </Button>
          <Button onClick={handleUpdateCatalog}>
            <RefreshCw className="h-4 w-4 mr-2" />
            Update Catalog
          </Button>
          <Button variant="outline" onClick={() => setShowCart(true)} className="relative">
            <ShoppingCart className="h-4 w-4 mr-2" />
            Cart
            {cartItemCount > 0 && (
              <Badge className="absolute -top-2 -right-2 h-5 w-5 rounded-full p-0 flex items-center justify-center text-xs">
                {cartItemCount}
              </Badge>
            )}
          </Button>
        </div>
      </div>

      <Tabs defaultValue="products" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="products">Products</TabsTrigger>
          <TabsTrigger value="categories">Categories</TabsTrigger>
          <TabsTrigger value="bundles">Bundles</TabsTrigger>
        </TabsList>

        <TabsContent value="products" className="space-y-6">
          <Card>
            <CardContent className="pt-6">
              <div className="flex flex-col lg:flex-row gap-4">
                <div className="flex-1">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                    <Input
                      placeholder="Search products..."
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      className="pl-10"
                    />
                  </div>
                </div>

                <div className="flex gap-2">
                  <Select value={selectedCategory} onValueChange={setSelectedCategory}>
                    <SelectTrigger className="w-40">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Categories</SelectItem>
                      {categories.map((category) => (
                        <SelectItem key={category.id} value={category.id}>
                          {category.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>

                  <Select value={sortBy} onValueChange={setSortBy}>
                    <SelectTrigger className="w-40">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="featured">Featured</SelectItem>
                      <SelectItem value="name">Name A-Z</SelectItem>
                      <SelectItem value="stock-high">Stock: High to Low</SelectItem>
                      <SelectItem value="stock-low">Stock: Low to High</SelectItem>
                    </SelectContent>
                  </Select>

                  <div className="flex border rounded-md">
                    <Button
                      variant={viewMode === "grid" ? "default" : "ghost"}
                      size="sm"
                      onClick={() => setViewMode("grid")}
                      className="rounded-r-none"
                    >
                      <Grid className="h-4 w-4" />
                    </Button>
                    <Button
                      variant={viewMode === "list" ? "default" : "ghost"}
                      size="sm"
                      onClick={() => setViewMode("list")}
                      className="rounded-l-none"
                    >
                      <List className="h-4 w-4" />
                    </Button>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>

          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <p className="text-sm text-muted-foreground">Showing {filteredProducts.length} products</p>
            </div>

            {filteredProducts.length === 0 ? (
              <div className="text-center py-12">
                <ShoppingCart className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-medium mb-2">No products found</h3>
                <p className="text-muted-foreground">
                  {products.length === 0
                    ? "No products available in the catalog yet."
                    : "Try adjusting your search or filter criteria."}
                </p>
              </div>
            ) : (
              <>
                {viewMode === "grid" ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                    {filteredProducts.map((product) => (
                      <Card key={product.id} className="group hover:shadow-lg transition-all duration-200">
                        <div className="relative overflow-hidden">
                          <img
                            src={product.image_url || "/placeholder.svg?height=200&width=300"}
                            alt={product.name || "Product"}
                            className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-200"
                          />
                          {product.current_stock <= 0 && (
                            <Badge variant="destructive" className="absolute top-2 right-2">
                              Out of Stock
                            </Badge>
                          )}
                          <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity">
                            <Button
                              size="sm"
                              variant="secondary"
                              onClick={() => handleAddToWishlist(product)}
                              className="h-8 w-8 p-0"
                            >
                              <Heart className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>

                        <CardContent className="p-4">
                          <div className="space-y-2">
                            <div className="flex items-start justify-between">
                              <h3 className="font-semibold text-sm line-clamp-2">
                                {product.name || "Unnamed Product"}
                              </h3>
                            </div>

                            <p className="text-xs text-muted-foreground line-clamp-2">{product.description}</p>

                            <div className="flex items-center gap-2">
                              <Badge
                                variant={product.current_stock > 0 ? "default" : "destructive"}
                                className="text-xs"
                              >
                                {product.current_stock > 0 ? `${product.current_stock} in stock` : "Out of Stock"}
                              </Badge>
                            </div>

                            <div className="flex gap-2 pt-2">
                              <Button
                                size="sm"
                                variant="outline"
                                className="flex-1 bg-transparent"
                                onClick={() => setSelectedProduct(product)}
                              >
                                <Eye className="h-3 w-3 mr-1" />
                                View
                              </Button>
                              <Button
                                size="sm"
                                className="flex-1"
                                disabled={product.current_stock <= 0}
                                onClick={() => handleAddToCart(product)}
                              >
                                <ShoppingCart className="h-3 w-3 mr-1" />
                                {product.current_stock > 0 ? "Add to Cart" : "Out of Stock"}
                              </Button>
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                ) : (
                  <div className="space-y-4">
                    {filteredProducts.map((product) => (
                      <Card key={product.id} className="hover:shadow-md transition-shadow">
                        <CardContent className="p-6">
                          <div className="flex gap-4">
                            <img
                              src={product.image_url || "/placeholder.svg?height=100&width=100"}
                              alt={product.name || "Product"}
                              className="w-24 h-24 object-cover rounded-lg"
                            />
                            <div className="flex-1 space-y-2">
                              <div className="flex items-start justify-between">
                                <div>
                                  <h3 className="font-semibold">{product.name || "Unnamed Product"}</h3>
                                  <p className="text-sm text-muted-foreground">{product.description}</p>
                                </div>
                                <div className="flex gap-2">
                                  {product.current_stock <= 0 && <Badge variant="destructive">Out of Stock</Badge>}
                                </div>
                              </div>

                              <div className="flex items-center justify-between">
                                <div className="flex items-center gap-2">
                                  <Badge variant={product.current_stock > 0 ? "default" : "destructive"}>
                                    {product.current_stock > 0
                                      ? `${product.current_stock} units available`
                                      : "Out of Stock"}
                                  </Badge>
                                </div>

                                <div className="flex gap-2">
                                  <Button variant="outline" size="sm" onClick={() => setSelectedProduct(product)}>
                                    <Eye className="h-4 w-4 mr-2" />
                                    View
                                  </Button>
                                  <Button variant="outline" size="sm" onClick={() => handleAddToWishlist(product)}>
                                    <Heart className="h-4 w-4" />
                                  </Button>
                                  <Button
                                    size="sm"
                                    disabled={product.current_stock <= 0}
                                    onClick={() => handleAddToCart(product)}
                                  >
                                    <ShoppingCart className="h-4 w-4 mr-2" />
                                    {product.current_stock > 0 ? "Add to Cart" : "Out of Stock"}
                                  </Button>
                                </div>
                              </div>
                            </div>
                          </div>
                        </CardContent>
                      </Card>
                    ))}
                  </div>
                )}
              </>
            )}
          </div>
        </TabsContent>

        <TabsContent value="categories" className="space-y-6">
          <div>
            <h2 className="text-xl font-semibold mb-4">Product Categories</h2>
            {categories.length === 0 ? (
              <div className="text-center py-12">
                <Grid className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
                <h3 className="text-lg font-medium mb-2">No categories found</h3>
                <p className="text-muted-foreground">Create some categories to organize your products.</p>
              </div>
            ) : (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {categories.map((category) => (
                  <Card key={category.id} className="hover:shadow-md transition-shadow cursor-pointer">
                    <div className="relative">
                      <img
                        src={category.image_url || "/placeholder.svg?height=200&width=300"}
                        alt={category.name || "Category"}
                        className="w-full h-48 object-cover rounded-t-lg bg-muted"
                      />
                    </div>
                    <CardHeader>
                      <CardTitle className="flex items-center justify-between">
                        {category.name || "Unnamed Category"}
                        <Badge variant="outline">{Number(category.product_count) || 0} items</Badge>
                      </CardTitle>
                    </CardHeader>
                    <CardContent>
                      <Button
                        variant="outline"
                        className="w-full bg-transparent"
                        onClick={() => {
                          setSelectedCategory(category.id)
                          // Use a more reliable way to switch to products tab
                          const tabsTriggers = document.querySelectorAll('[role="tab"]')
                          const productsTab = Array.from(tabsTriggers).find(
                            (tab) => tab.textContent?.trim() === "Products",
                          ) as HTMLElement
                          if (productsTab) {
                            productsTab.click()
                          }

                          toast({
                            title: "Viewing Category",
                            description: `Showing products from ${category.name}`,
                            duration: 2000,
                          })
                        }}
                      >
                        <Eye className="h-4 w-4 mr-2" />
                        View Products
                      </Button>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </div>
        </TabsContent>

        <TabsContent value="bundles" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Product Bundles</CardTitle>
              <CardDescription>
                Special product combinations with discounted pricing (10% off when buying multiple items from the same
                category)
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {categories.slice(0, 2).map((category) => {
                  const categoryProducts = products
                    .filter((p) => p.category_id === category.id && p.is_active)
                    .slice(0, 3)
                  if (categoryProducts.length < 2) return null

                  const bundlePrice = categoryProducts.reduce((sum, p) => sum + (Number(p.selling_price) || 0), 0)
                  const discountedPrice = Math.floor(bundlePrice * 0.9)

                  return (
                    <Card key={category.id} className="border-2 border-dashed border-primary/20">
                      <CardContent className="p-6">
                        <div className="flex items-center justify-between mb-4">
                          <h3 className="font-semibold">{category.name || "Unnamed Category"} Bundle</h3>
                          <div className="text-right">
                            <Badge variant="secondary">Save 10%</Badge>
                          </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
                          {categoryProducts.map((product, index) => (
                            <div key={product.id} className="flex items-center gap-2">
                              <img
                                src={product.image_url || "/placeholder.svg?height=50&width=50"}
                                alt={product.name || "Product"}
                                className="w-12 h-12 object-cover rounded"
                              />
                              <div className="flex-1">
                                <p className="text-sm font-medium">{product.name || "Unnamed Product"}</p>
                                <p className="text-xs text-muted-foreground">{product.current_stock || 0} in stock</p>
                              </div>
                              {index < categoryProducts.length - 1 && <span className="text-muted-foreground">+</span>}
                            </div>
                          ))}
                        </div>

                        <Button className="w-full bg-transparent" variant="outline">
                          <ShoppingCart className="h-4 w-4 mr-2" />
                          Add Bundle to Cart
                        </Button>
                      </CardContent>
                    </Card>
                  )
                })}

                {categories.length === 0 && (
                  <div className="text-center py-8 text-muted-foreground">
                    <ShoppingCart className="h-12 w-12 mx-auto mb-2 opacity-50" />
                    <p>No bundles available - add more products to create bundles</p>
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>

      {/* Product Detail Modal */}
      {selectedProduct && <ProductDetailModal product={selectedProduct} onClose={() => setSelectedProduct(null)} />}

      {showCart && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-2xl max-h-[90vh] overflow-hidden">
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>Shopping Cart ({cartItemCount} items)</span>
                <Button variant="ghost" size="sm" onClick={() => setShowCart(false)}>
                  ×
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent className="overflow-y-auto space-y-4">
              {cartItems.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  <ShoppingCart className="h-12 w-12 mx-auto mb-2 opacity-50" />
                  <p>Your cart is empty</p>
                </div>
              ) : (
                <>
                  <div className="space-y-4">
                    {cartItems.map((item) => (
                      <div key={item.id} className="flex items-center gap-4 p-4 border rounded-lg">
                        <img
                          src={item.product?.image_url || "/placeholder.svg?height=60&width=60"}
                          alt={item.product?.name || "Product"}
                          className="w-16 h-16 object-cover rounded"
                        />
                        <div className="flex-1">
                          <h4 className="font-medium">{item.product?.name || "Unnamed Product"}</h4>
                          <p className="text-sm text-muted-foreground">Quantity: {item.quantity}</p>
                        </div>
                        <div className="flex items-center gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => updateCartQuantity(item.id, item.quantity - 1)}
                          >
                            <Minus className="h-3 w-3" />
                          </Button>
                          <span className="w-8 text-center">{item.quantity}</span>
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => updateCartQuantity(item.id, item.quantity + 1)}
                          >
                            <Plus className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => removeCartItem(item.id)}
                            className="text-red-600"
                          >
                            <X className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>

                  <div className="border-t pt-4 space-y-3">
                    <div className="flex justify-between items-center">
                      <span className="font-semibold text-lg">Total Items: {cartItemCount}</span>
                    </div>

                    <Button variant="outline" className="w-full bg-transparent" onClick={shareCartViaWhatsApp}>
                      <MessageCircle className="h-4 w-4 mr-2" />
                      Share Cart via WhatsApp
                    </Button>

                    <Button className="w-full" onClick={handlePayNow}>
                      <Receipt className="h-4 w-4 mr-2" />
                      Pay Now - PKR {cartTotal.toLocaleString()}
                    </Button>
                  </div>
                </>
              )}
            </CardContent>
          </Card>
        </div>
      )}

      {showReceiptModal && (
        <CreateReceiptModal
          onClose={() => setShowReceiptModal(false)}
          onCreateReceipt={(receipt) => {
            console.log("[v0] Receipt created:", receipt)
            setShowReceiptModal(false)
            setCartItems([])
            toast({
              title: "Receipt Created Successfully! 🧾",
              description: `Receipt ${receipt.receiptNumber} for ${receipt.customerName} has been generated. Cart has been cleared.`,
              duration: 5000,
            })
          }}
          cartItems={cartItems}
        />
      )}

      {cartItemCount > 0 && (
        <div className="fixed bottom-0 left-0 right-0 bg-white border-t shadow-lg p-4 z-40">
          <div className="max-w-7xl mx-auto flex items-center justify-between">
            <div className="flex items-center gap-4">
              <Button variant="outline" onClick={() => setShowCart(true)}>
                <ShoppingCart className="h-4 w-4 mr-2" />
                View Cart ({cartItemCount})
              </Button>
              <Button variant="outline" onClick={shareCartViaWhatsApp}>
                <MessageCircle className="h-4 w-4 mr-2" />
                Share via WhatsApp
              </Button>
            </div>
            <Button onClick={handlePayNow} className="bg-green-600 hover:bg-green-700">
              <Receipt className="h-4 w-4 mr-2" />
              Pay Now - PKR {cartTotal.toLocaleString()}
            </Button>
          </div>
        </div>
      )}
    </div>
  )
}

components/pages/receipts.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Input } from "@/components/ui/input"
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs"
import { CreateReceiptModal } from "@/components/modals/create-receipt-modal"
import { ReceiptDetailModal } from "@/components/modals/receipt-detail-modal"
import { Plus, Eye, Download, Search, Calendar, Printer } from "lucide-react"
import { useToast } from "@/hooks/use-toast"
import { syncManager } from "@/lib/sync-manager"
import { PDFGenerator } from "@/lib/pdf-generator"

export function Receipts() {
  const [receipts, setReceipts] = useState<any[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [paymentFilter, setPaymentFilter] = useState("all")
  const [dateFilter, setDateFilter] = useState("all")
  const [selectedReceipt, setSelectedReceipt] = useState<any | null>(null)
  const [showCreateModal, setShowCreateModal] = useState(false)
  const [activeTab, setActiveTab] = useState("all")
  const { toast } = useToast()

  useEffect(() => {
    const loadReceipts = async () => {
      try {
        setLoading(true)
        const receiptsData = await syncManager.getReceipts()
        setReceipts(receiptsData)
      } catch (error) {
        console.error("[v0] Failed to load receipts:", error)
        toast({
          title: "Error",
          description: "Failed to load receipts. Please try again.",
          variant: "destructive",
        })
      } finally {
        setLoading(false)
      }
    }

    loadReceipts()
  }, [toast])

  const handleCreateReceipt = async (newReceipt: any) => {
    try {
      await syncManager.saveReceipt(newReceipt)
      setReceipts((prev) => [...prev, newReceipt])
      toast({
        title: "Receipt Created",
        description: `Receipt ${newReceipt.receiptNumber || newReceipt.id} has been saved successfully.`,
      })
    } catch (error) {
      console.error("[v0] Failed to save receipt:", error)
      toast({
        title: "Error",
        description: "Failed to save receipt. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleDeleteReceipt = async (receiptId: string) => {
    try {
      await syncManager.deleteReceipt(receiptId)
      setReceipts((prev) => prev.filter((r) => r.id !== receiptId))
      toast({
        title: "Receipt Deleted",
        description: "Receipt has been permanently deleted.",
      })
    } catch (error) {
      console.error("[v0] Failed to delete receipt:", error)
      toast({
        title: "Error",
        description: "Failed to delete receipt. Please try again.",
        variant: "destructive",
      })
    }
  }

  const handleCreateReturn = (returnData: any) => {
    // Dispatch custom event to notify Returns page
    window.dispatchEvent(new CustomEvent("new-return-created", { detail: returnData }))

    toast({
      title: "Return Recorded",
      description: `Return for ${returnData.productName} has been recorded in the Returns section.`,
    })
  }

  // Filter receipts based on search and filters
  const filteredReceipts = receipts.filter((receipt) => {
    const matchesSearch =
      receipt.id.toLowerCase().includes(searchTerm.toLowerCase()) ||
      receipt.customerName.toLowerCase().includes(searchTerm.toLowerCase()) ||
      receipt.customerPhone.includes(searchTerm)

    const matchesPayment = paymentFilter === "all" || receipt.paymentMethod === paymentFilter

    const matchesTab =
      activeTab === "all" ||
      (activeTab === "today" && new Date(receipt.date).toDateString() === new Date().toDateString())

    return matchesSearch && matchesPayment && matchesTab
  })

  const handlePrintReceipt = (receiptId: string) => {
    toast({
      title: "Printing Receipt",
      description: `Receipt ${receiptId} is being prepared for printing.`,
    })
  }

  const handleDownloadReceipt = (receipt: any) => {
    try {
      PDFGenerator.generateReceipt(receipt)
      toast({
        title: "Receipt Downloaded",
        description: `Receipt ${receipt.receiptNumber || receipt.id} PDF has been downloaded.`,
      })
    } catch (error) {
      console.error("[v0] Failed to generate PDF:", error)
      toast({
        title: "Error",
        description: "Failed to generate PDF. Please try again.",
        variant: "destructive",
      })
    }
  }

  // Calculate summary stats
  const todayReceipts = receipts.filter((r) => new Date(r.date).toDateString() === new Date().toDateString())
  const todayRevenue = todayReceipts.reduce((sum, r) => sum + r.total, 0)

  if (loading) {
    return (
      <div className="space-y-6">
        <div className="flex justify-center items-center h-64">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">Loading receipts...</p>
          </div>
        </div>
      </div>
    )
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold">Receipts</h1>
          <p className="text-muted-foreground">Manage sales receipts and transactions</p>
        </div>
        <Button onClick={() => setShowCreateModal(true)}>
          <Plus className="h-4 w-4 mr-2" />
          New Receipt
        </Button>
      </div>

      {/* Summary Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold">{todayReceipts.length}</div>
            <p className="text-xs text-muted-foreground">Today's Receipts</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold">PKR {todayRevenue.toLocaleString()}</div>
            <p className="text-xs text-muted-foreground">Today's Revenue</p>
          </CardContent>
        </Card>
        <Card>
          <CardContent className="pt-6">
            <div className="text-2xl font-bold">{receipts.length}</div>
            <p className="text-xs text-muted-foreground">Total Receipts</p>
          </CardContent>
        </Card>
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="all">All Receipts</TabsTrigger>
          <TabsTrigger value="today">Today</TabsTrigger>
        </TabsList>

        <TabsContent value={activeTab} className="space-y-4">
          {/* Search and Filters */}
          <Card>
            <CardContent className="pt-6">
              <div className="flex flex-col sm:flex-row gap-4">
                <div className="relative flex-1">
                  <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                  <Input
                    placeholder="Search receipts by ID, customer name, or phone..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="pl-10"
                  />
                </div>
                <div className="flex gap-2">
                  <Select value={paymentFilter} onValueChange={setPaymentFilter}>
                    <SelectTrigger className="w-32">
                      <SelectValue placeholder="Payment" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Methods</SelectItem>
                      <SelectItem value="cash">Cash</SelectItem>
                      <SelectItem value="card">Card</SelectItem>
                      <SelectItem value="upi">UPI</SelectItem>
                    </SelectContent>
                  </Select>
                  <Button variant="outline">
                    <Calendar className="h-4 w-4 mr-2" />
                    Date Range
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Receipts List */}
          <div className="space-y-4">
            {filteredReceipts.map((receipt) => (
              <Card key={receipt.id} className="hover:shadow-md transition-shadow">
                <CardContent className="pt-6">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <div>
                        <h3 className="font-semibold">Receipt #{receipt.receiptNumber || receipt.id}</h3>
                        <p className="text-sm text-muted-foreground">{receipt.customerName}</p>
                        <p className="text-xs text-muted-foreground">{receipt.customerPhone}</p>
                      </div>
                      <div>
                        <p className="text-lg font-bold">PKR {receipt.total.toLocaleString()}</p>
                        <p className="text-sm text-muted-foreground">
                          {receipt.items.reduce((sum, item) => sum + item.quantity, 0)} items
                        </p>
                        <p className="text-xs text-muted-foreground capitalize">{receipt.paymentMethod}</p>
                      </div>
                    </div>
                    <div className="flex items-center gap-4">
                      <div className="text-right">
                        <p className="text-sm text-muted-foreground mt-1">
                          {new Date(receipt.date).toLocaleDateString()} {new Date(receipt.date).toLocaleTimeString()}
                        </p>
                      </div>
                      <div className="flex gap-2">
                        <Button variant="ghost" size="sm" onClick={() => setSelectedReceipt(receipt)}>
                          <Eye className="h-4 w-4" />
                        </Button>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => handlePrintReceipt(receipt.receiptNumber || receipt.id)}
                        >
                          <Printer className="h-4 w-4" />
                        </Button>
                        <Button variant="ghost" size="sm" onClick={() => handleDownloadReceipt(receipt)}>
                          <Download className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                  {receipt.notes && (
                    <div className="mt-3 p-2 bg-muted rounded text-sm">
                      <strong>Notes:</strong> {receipt.notes}
                    </div>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>
        </TabsContent>
      </Tabs>

      {/* Modals */}
      {showCreateModal && (
        <CreateReceiptModal
          onClose={() => setShowCreateModal(false)}
          onCreateReceipt={handleCreateReceipt}
          onCreateReturn={handleCreateReturn}
        />
      )}
      {selectedReceipt && (
        <ReceiptDetailModal
          receipt={selectedReceipt}
          onClose={() => setSelectedReceipt(null)}
          onDelete={handleDeleteReceipt}
        />
      )}
    </div>
  )
}

components/pages/shopkeepers.tsx.content:
"use client"

import { useState, useEffect } from "react"
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card"
import { Button } from "@/components/ui/button"
import { Badge } from "@/components/ui/badge"
import { Input } from "@/components/ui/input"
import { Plus, Search, Phone, Eye, CreditCard, Calendar } from "lucide-react"
import { syncManager } from "@/lib/sync-manager"

interface PendingReceipt {
  receiptNumber: string
  receiptDate: string
  totalAmount: number
  amountReceived: number
  pendingAmount: number
}

interface Shopkeeper {
  id: number
  name: string
  phone: string
  balance: number
  totalOrders: number
  status: string
  pendingReceipts: PendingReceipt[]
  totalPendingAmount: number
}

export function Shopkeepers() {
  const [shopkeepers, setShopkeepers] = useState<Shopkeeper[]>([])
  const [selectedShopkeeper, setSelectedShopkeeper] = useState<Shopkeeper | null>(null)
  const [paymentAmount, setPaymentAmount] = useState("")

  useEffect(() => {
    const loadShopkeepers = async () => {
      try {
        const shopkeepersData = await syncManager.getShopkeepers()
        console.log("[v0] Loaded shopkeepers from database:", shopkeepersData)

        if (shopkeepersData && shopkeepersData.length > 0) {
          const formattedShopkeepers = await Promise.all(
            shopkeepersData.map(async (sk: any) => {
              // Get receipts for this shopkeeper to calculate balance
              const receipts = await syncManager.getReceipts()
              const shopkeeperReceipts = receipts.filter(
                (receipt: any) => receipt.customer_name === sk.name && receipt.customer_phone === sk.contact,
              )

              const totalOrders = shopkeeperReceipts.length
              const totalAmount = shopkeeperReceipts.reduce(
                (sum: number, receipt: any) => sum + (receipt.total_amount || 0),
                0,
              )
              const totalReceived = shopkeeperReceipts.reduce(
                (sum: number, receipt: any) => sum + (receipt.amount_received || 0),
                0,
              )
              const pendingAmount = totalAmount - totalReceived

              const pendingReceipts = shopkeeperReceipts
                .filter((receipt: any) => (receipt.total_amount || 0) > (receipt.amount_received || 0))
                .map((receipt: any) => ({
                  receiptNumber: receipt.receipt_number,
                  receiptDate: receipt.receipt_date,
                  totalAmount: receipt.total_amount || 0,
                  amountReceived: receipt.amount_received || 0,
                  pendingAmount: (receipt.total_amount || 0) - (receipt.amount_received || 0),
                }))

              return {
                id: sk.id,
                name: sk.name,
                phone: sk.contact || sk.phone || "",
                balance: sk.current_balance || totalReceived - totalAmount, // Negative if customer owes money
                totalOrders: totalOrders,
                status: sk.is_active ? "active" : "inactive",
                pendingReceipts: pendingReceipts,
                totalPendingAmount: pendingAmount,
              }
            }),
          )
          setShopkeepers(formattedShopkeepers)
        }
      } catch (error) {
        console.error("[v0] Failed to load shopkeepers:", error)
      }
    }

    loadShopkeepers()

    const handleBalanceUpdate = (event: CustomEvent) => {
      const {
        shopkeeperName,
        shopkeeperPhone,
        receiptNumber,
        receiptDate,
        totalAmount,
        amountReceived,
        pendingAmount,
      } = event.detail

      setShopkeepers((prev) => {
        const existingShopkeeper = prev.find((s) => s.name === shopkeeperName && s.phone === shopkeeperPhone)

        if (existingShopkeeper) {
          return prev.map((s) =>
            s.id === existingShopkeeper.id
              ? {
                  ...s,
                  pendingReceipts: [
                    ...s.pendingReceipts,
                    {
                      receiptNumber,
                      receiptDate,
                      totalAmount,
                      amountReceived,
                      pendingAmount,
                    },
                  ],
                  totalPendingAmount: s.totalPendingAmount + pendingAmount,
                }
              : s,
          )
        } else {
          const newShopkeeper: Shopkeeper = {
            id: Date.now(),
            name: shopkeeperName,
            phone: shopkeeperPhone,
            balance: 0,
            totalOrders: 1,
            status: "active",
            pendingReceipts: [
              {
                receiptNumber,
                receiptDate,
                totalAmount,
                amountReceived,
                pendingAmount,
              },
            ],
            totalPendingAmount: pendingAmount,
          }
          return [...prev, newShopkeeper]
        }
      })
    }

    window.addEventListener("update-shopkeeper-balance", handleBalanceUpdate as EventListener)
    return () => window.removeEventListener("update-shopkeeper-balance", handleBalanceUpdate as EventListener)
  }, [])

  const handlePayment = (shopkeeperId: number, receiptNumber: string) => {
    const amount = Number.parseFloat(paymentAmount)
    if (!amount || amount <= 0) return

    setShopkeepers((prev) =>
      prev.map((shopkeeper) => {
        if (shopkeeper.id === shopkeeperId) {
          const updatedReceipts = shopkeeper.pendingReceipts
            .map((receipt) => {
              if (receipt.receiptNumber === receiptNumber) {
                const newPendingAmount = Math.max(0, receipt.pendingAmount - amount)
                return {
                  ...receipt,
                  amountReceived: receipt.amountReceived + Math.min(amount, receipt.pendingAmount),
                  pendingAmount: newPendingAmount,
                }
              }
              return receipt
            })
            .filter((receipt) => receipt.pendingAmount > 0)

          const newTotalPending = updatedReceipts.reduce((sum, receipt) => sum + receipt.pendingAmount, 0)

          return {
            ...shopkeeper,
            pendingReceipts: updatedReceipts,
            totalPendingAmount: newTotalPending,
          }
        }
        return shopkeeper
      }),
    )

    setPaymentAmount("")
  }

  return (
    <div className="space-y-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div>
          <h1 className="text-2xl font-bold">Shopkeepers</h1>
          <p className="text-muted-foreground">Manage registered shopkeepers and their accounts</p>
        </div>
        <Button>
          <Plus className="h-4 w-4 mr-2" />
          Add Shopkeeper
        </Button>
      </div>

      <Card>
        <CardContent className="pt-6">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground" />
            <Input placeholder="Search shopkeepers..." className="pl-10" />
          </div>
        </CardContent>
      </Card>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
        {shopkeepers.map((shopkeeper) => (
          <Card key={shopkeeper.id}>
            <CardContent className="pt-6">
              <div className="space-y-4">
                <div className="flex items-center justify-between">
                  <h3 className="font-semibold">{shopkeeper.name}</h3>
                  <Badge variant={shopkeeper.status === "active" ? "default" : "outline"}>{shopkeeper.status}</Badge>
                </div>

                <div className="space-y-2">
                  <div className="flex items-center gap-2">
                    <Phone className="h-4 w-4 text-muted-foreground" />
                    <span className="text-sm">{shopkeeper.phone}</span>
                  </div>

                  <div className="flex justify-between">
                    <span className="text-sm text-muted-foreground">Balance:</span>
                    <span
                      className={`text-sm font-medium ${shopkeeper.balance >= 0 ? "text-green-600" : "text-red-600"}`}
                    >
                      PKR {Math.abs(shopkeeper.balance).toLocaleString()}
                      {shopkeeper.balance < 0 && " (Due)"}
                    </span>
                  </div>

                  {shopkeeper.totalPendingAmount > 0 && (
                    <div className="flex justify-between">
                      <span className="text-sm text-muted-foreground">Pending Amount:</span>
                      <span className="text-sm font-medium text-red-600">
                        PKR {shopkeeper.totalPendingAmount.toLocaleString()}
                      </span>
                    </div>
                  )}

                  <div className="flex justify-between">
                    <span className="text-sm text-muted-foreground">Total Orders:</span>
                    <span className="text-sm font-medium">{shopkeeper.totalOrders}</span>
                  </div>
                </div>

                <Button
                  variant="outline"
                  className="w-full bg-transparent"
                  onClick={() => setSelectedShopkeeper(shopkeeper)}
                >
                  <Eye className="h-4 w-4 mr-2" />
                  View Details
                </Button>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {selectedShopkeeper && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center p-4 z-50">
          <Card className="w-full max-w-2xl max-h-[90vh] overflow-hidden">
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>{selectedShopkeeper.name} - Payment Details</span>
                <Button variant="ghost" size="sm" onClick={() => setSelectedShopkeeper(null)}>
                  ×
                </Button>
              </CardTitle>
            </CardHeader>
            <CardContent className="overflow-y-auto space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <p className="text-sm text-muted-foreground">Phone</p>
                  <p className="font-medium">{selectedShopkeeper.phone}</p>
                </div>
                <div>
                  <p className="text-sm text-muted-foreground">Total Pending</p>
                  <p className="font-medium text-red-600">
                    PKR {selectedShopkeeper.totalPendingAmount.toLocaleString()}
                  </p>
                </div>
              </div>

              {selectedShopkeeper.pendingReceipts.length > 0 && (
                <div className="space-y-3">
                  <h4 className="font-semibold">Pending Receipts</h4>
                  {selectedShopkeeper.pendingReceipts.map((receipt) => (
                    <Card key={receipt.receiptNumber} className="border-red-200">
                      <CardContent className="pt-4">
                        <div className="space-y-3">
                          <div className="flex justify-between items-center">
                            <div>
                              <p className="font-medium">{receipt.receiptNumber}</p>
                              <p className="text-sm text-muted-foreground flex items-center gap-1">
                                <Calendar className="h-3 w-3" />
                                {new Date(receipt.receiptDate).toLocaleDateString()}
                              </p>
                            </div>
                            <Badge variant="destructive">Pending</Badge>
                          </div>

                          <div className="grid grid-cols-3 gap-2 text-sm">
                            <div>
                              <p className="text-muted-foreground">Total</p>
                              <p className="font-medium">PKR {receipt.totalAmount.toLocaleString()}</p>
                            </div>
                            <div>
                              <p className="text-muted-foreground">Received</p>
                              <p className="font-medium text-green-600">
                                PKR {receipt.amountReceived.toLocaleString()}
                              </p>
                            </div>
                            <div>
                              <p className="text-muted-foreground">Pending</p>
                              <p className="font-medium text-red-600">PKR {receipt.pendingAmount.toLocaleString()}</p>
                            </div>
                          </div>

                          <div className="flex gap-2">
                            <Input
                              type="number"
                              placeholder="Payment amount"
                              value={paymentAmount}
                              onChange={(e) => setPaymentAmount(e.target.value)}
                              className="flex-1"
                            />
                            <Button
                              size="sm"
                              onClick={() => handlePayment(selectedShopkeeper.id, receipt.receiptNumber)}
                              disabled={!paymentAmount || Number.parseFloat(paymentAmount) <= 0}
                            >
                              <CreditCard className="h-4 w-4 mr-1" />
                              Pay
                            </Button>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              )}

              {selectedShopkeeper.pendingReceipts.length === 0 && (
                <div className="text-center py-8 text-muted-foreground">
                  <CreditCard className="h-12 w-12 mx-auto mb-2 opacity-50" />
                  <p>No pending payments</p>
                </div>
              )}
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  )
}

